import request from '@/utils/request';
/**
 * 新增
 * @param {
 * content (string): 内容 ,
 * merchantId (integer): 商户号 ,
 * publishEndTime (string): 发布结束时间 ,
 * publishStartTime (string): 发布开始时间 ,
 * title (string): 标题 ,
 * type (integer): 类型:1滚动公告、2弹出公告
 * }
 */

export function noticeCreate(data) {
  return request({
    url: '/notice/create',
    method: 'post',
    data: data
  });
}
/**
 * 查询列表
 * @param {
 * createdBy (string, optional): 创建人 ,
 * createdEndTime (string, optional): 创建结束时间 ,
 * createdStartTime (string, optional): 创建开始时间 ,
 * merchantId (integer): 商户号 ,
 * publishEndTime (string, optional): 发布结束时间 ,
 * publishStartTime (string, optional): 发布开始时间
 * }
 */

export function noticeList(params) {
  return request({
    url: '/notice/search',
    method: 'get',
    params: params
  });
}
/*
 * 根据ID查询详情
 * @param {ip:string}
 */

export function noticeQueryById(params) {
  return request({
    url: '/notice/queryById',
    method: 'get',
    params: params
  });
}
/**
 * 更新数据
 * @param {
 * content (string): 内容 ,
 * id (string): 标题 ,
 * publishEndTime (string): 发布结束时间 ,
 * publishStartTime (string): 发布开始时间 ,
 * title (string): 标题 ,
 * type (integer): 类型:1滚动公告、2弹出公告
 * status (integer): 1发布中、2待发布、3已过期、4已停用}
 */

export function noticeUpdate(data) {
  return request({
    url: '/notice/update',
    method: 'post',
    data: data
  });
}
/**
 * 更新状态
 * @param {
 * id (integer): 公告ID ,
 * status (integer): 1发布中、2待发布、3已过期、4已停用}
 */

export function noticeUpdateStatus(data) {
  return request({
    url: '/notice/updateStatus',
    method: 'post',
    data: data
  });
}
export default {
  noticeUpdate: noticeUpdate,
  noticeUpdateStatus: noticeUpdateStatus,
  noticeQueryById: noticeQueryById,
  noticeList: noticeList,
  noticeCreate: noticeCreate
};
