import request from '@/utils/request';
var userApi = {
  Login: '/ums/user/login',
  Logout: '/ums/user/logout',
  UserInfo: '/ums/user/info',
  ChangePassword: '/ums/user/update'
};
/**
 * login func
 * parameter: {
 *     username: '',
 *     password: '',
 *     remember_me: true,
 *     captcha: '12345'
 * }
 * @param parameter
 * @returns {*}
 */

export function login(parameter) {
  return request({
    url: userApi.Login,
    method: 'post',
    data: parameter
  });
}
export function changePassword(parameter) {
  return request({
    url: userApi.ChangePassword,
    method: 'post',
    data: parameter
  });
}
export function getInfo() {
  return request({
    url: userApi.UserInfo,
    method: 'get',
    headers: {
      'Content-Type': 'application/json;charset=UTF-8'
    }
  });
}
export function logout() {
  return request({
    url: userApi.Logout,
    method: 'post',
    headers: {
      'Content-Type': 'application/json;charset=UTF-8'
    }
  });
}
