import Cookies from 'js-cookie';
import store from 'storejs';
var TokenKey = 'b2b-token-key';
var FirstTokenKey = 'b2b-first-token-key';
var UserInfoKey = 'zr-user-key';
export function getToken() {
  return Cookies.get(TokenKey);
}
export function setToken(token) {
  return Cookies.set(TokenKey, token);
}
export function getFirstToken() {
  return Cookies.get(FirstTokenKey);
}
export function setFirstToken(token) {
  return Cookies.set(FirstTokenKey, token);
}
export function removeFirstToken() {
  return Cookies.remove(FirstTokenKey);
}
export function removeToken() {
  return Cookies.remove(TokenKey);
}
export function getUserName() {
  var userInfo = store.get(UserInfoKey) || {};
  return userInfo.username;
}
export function getUserInfo() {
  var userInfo = store.get(UserInfoKey) || null;
  return userInfo;
}
export function setUserInfo(UserInfo) {
  return store.set(UserInfoKey, UserInfo);
}
export function removeUserInfo() {
  return store.remove(UserInfoKey);
}
