import { routerSwitchNames } from '@/utils/consts'; // import defaultSettings from '@/settings'

import i18n from '@/utils/lang';
export function generateTitle(title) {
  var hasKey = routerSwitchNames[title];

  if (hasKey) {
    // $t :this method from vue-i18n, inject in @/lang/index.js
    var translatedTitle = routerSwitchNames[title];
    console.log('translatedTitle :>> ', translatedTitle);
    return translatedTitle;
  }

  return title;
} // used in route / breadcrumb / tagsview

