import request from '@/utils/request'; // 查询红包雨记录

export function getEnvelopeRecord(params) {
  return request({
    url: '/operation/getEnvelopeRecord',
    method: 'get',
    params: params
  });
}
export function getActivityTypeList(params) {
  return request({
    url: '/operation/getActivityTypeList',
    method: 'get',
    params: params
  });
}
export function getRebateList(params) {
  return request({
    url: '/activity/rebate',
    method: 'get',
    params: params
  });
}
export default {
  getEnvelopeRecord: getEnvelopeRecord,
  getRebateList: getRebateList,
  getActivityTypeList: getActivityTypeList
};
