import { mapGetters } from 'vuex'
import list from '@/mixins/list'
export default {
	name: 'MerchantTableComp',
	mixins: [list],
	props: {
		tableData: {
			type: Object,
			default: function() {
				return {}
			}
		},
		currency: {
			type: Array,
			default: function() {
				return []
			}
		}
	},
	data() {
		return {
			pageSize: 5,
			currentAgentId: ''
		}
	},
	computed: {
		...mapGetters(['userInfo', 'globalDics'])
	},
	methods: {
		ifArrow(levels, index) {
			const levelsLength = levels.length
			if (levelsLength > 1 && index !== levelsLength - 1) return true
		},
		getCooperationModel(model) {
			if (model === 1) {
				return this.$t('merchant_merchantinfo_components_tablecomp_1096')
			} else if (model === 2) {
				return this.$t('merchant_merchantinfo_components_tablecomp_1097')
			} else if (model === 3) {
				return this.$t('merchant_merchantinfo_components_tablecomp_1098')
			}
		},
		editTrialInfo(index, row) {
			this.$emit('editTrialInfo', index, row, this.tableData.level)
		},
		setting(index, row) {
			this.$emit('setting', index, row, this.tableData.level)
		},
		resetPassword(row) {
			this.$emit('resetPassword', row)
		},
		editAccountInfo(index, row, type) {
			this.$emit('editAccountInfo', index, row, type, this.tableData.level)
		},
		editOpen(index, row, level) {
			this.$emit('editOpen', index, row, this.tableData.level)
		},
		editAgentStatus(index, row) {
			this.$emit('editAgentStatus', index, row, this.tableData.level)
		},
		Unlock(index, row) {
			this.$emit('Unlock', index, row, this.tableData.level)
		},
		showKeysManage(row) {
			this.$emit('showKeysManage', row)
		},
		handlePaginationChange(val, type) {
			this.$emit(
				'modifyData',
				this.tableData.level,
				{
					pageSize: type === 1 ? val : this.pageSize,
					pageIndex: type === 2 ? val : this.pageIndex,
					agentId: this.tableData.parentId,
					agentName: this.tableData.parentName
				},
				this.tableData.level - 1,
				2
			)
		},
		showChildren(level, opened, row) {
			this.$emit(
				'showChildren',
				this.currentAgentId === row.agentId,
				level,
				opened,
				{
					agentId: row.agentId,
					agentName: row.agentName,
					pageSize: 5,
					pageIndex: 1
				}
			)
			this.currentAgentId = row.agentId
		},
		handleNewBusiness(type, row) {
			if (Number(row.availableAccountLimit) === 0) {
				this.$message({
					type: 'error',
					message: this.$t('wallet_10')
				})
				return
			}
			this.$emit(
				'handleNewBusiness',
				type,
				row.agentsLevel,
				'bottom',
				row.apiRate,
				row.agentId,
				row.currency,
				row.countPercentage,
				row.cooperationModel,
				row.agentType,
				row.availableAccountLimit,
				row.platformAgent
			)
		},

		// 获取货币类型
		getCurrency(id) {
			const index = this.currency.findIndex((val) => Number(val.id) === id)
			if (index > -1) {
				return this.currency[index].code
			}
		}
	}
}
