export default {
	name: 'EditTrial',
	props: {
		accountData: {
			type: Object,
			default: () => {}
		},
		type: {
			type: String,
			default: ''
		},
		value: {
			type: Boolean,
			default: false
		},
		editTrialInfoSuccess: {
			type: Function,
			default: function() {}
		}
	},
	data() {
		return {
			accountForm: {}
		}
	},
	computed: {
		accountFormRules() {
			return {
				rewardAnthorAgentRate: [
					{
						required: true,
						trigger: 'blur',
						message: this.$t('new_67')
					}
				],
				rewardDealerAgentRate: [
					{
						required: true,
						trigger: 'blur',
						message: this.$t('new_67')
					}
				],
				rewardTableAnchorAgentRate: [
					{
						required: true,
						trigger: 'blur',
						message: this.$t('new_67')
					}
				]
			}
		},
		modalTitle() {
			let text
			switch (this.type) {
				case 'rewardTableAnchorAgentRate':
					text = this.$t('new_36')
					break
				case 'rewardAnthorAgentRate':
					text = this.$t('new_36666')
					break
				case 'rewardDealerAgentRate':
					text = this.$t('new_37')
					break
			}
			return text
		}
	},
	watch: {
		accountData(val) {
			this.accountForm = {
				...val
			}
			this.accountForm[this.type] = (this.accountForm[this.type] * 100).toFixed(0)
		}
	},
	methods: {
		handleClose() {
			this.$refs.accountForm.clearValidate()
			this.$emit('input', false)
		},
		setLimit() {
			if (!/^([1-9]?\d|100)$/.test(Number(this.accountForm[[this.type]]))) {
				this.$message({
					message: this.$t('new_67'),
					type: 'error'
				})
				return
			}
			this.$refs.accountForm.validate((valid) => {
				if (valid) {
					const form = {
						agentId: this.accountForm.agentId
					}
					form[this.type] = (Number(this.accountForm[this.type]) / 100).toFixed(2)
					this.$api.updateAgentSetting(form).then((_) => {
						if (_.code === 200) {
							this.$emit('input', false)
							this.$emit('closeEdit', Number(this.accountForm[this.type]))
							this.$message({
								message: this.$t(
									this.$t('merchant_merchantinfo_components_edittrial_987')
								),
								type: 'success'
							})
						}
					})
				}
			})
		}
	}
}
