import { mapGetters } from 'vuex'
import list from '@/mixins/list'
export default {
	name: 'MerchantTableComp',
	mixins: [list],
	props: {
		tableData: {
			type: Object,
			default: function() {
				return {}
			}
		}
	},
	data() {
		return {
			pageSize: 5,
			currentAgentId: '',
			currentType: ''
		}
	},
	computed: {
		...mapGetters(['userInfo'])
	},
	methods: {
		ifArrow(levels, index) {
			const levelsLength = levels.length
			if (levelsLength > 1 && index !== levelsLength - 1) return true
		},
		checkRecord(row) {
			this.$emit('checkRecord', row)
		},
		accountRecharge(index, row) {
			this.$emit('accountRecharge', index, row, this.tableData.level)
		},
		handlePaginationChange(val, type) {
			this.$emit(
				'modifyData',
				this.tableData.level,
				{
					pageSize: type === 1 ? val : this.pageSize,
					pageIndex: type === 2 ? val : this.pageIndex,
					agentId: this.tableData.parentId,
					agentName: this.tableData.parentName,
					accountType: this.tableData.accountType
				},
				this.tableData.level - 1,
				2
			)
		},
		showChildren(level, opened, row) {
			this.$emit(
				'showChildren',
				this.currentAgentId === row.agentId && this.currentType === row.accountType,
				level,
				opened,
				{
					accountType: row.accountType,
					agentId: row.agentId,
					agentName: row.agentName,
					pageSize: 5,
					pageIndex: 1
				}
			)
			this.currentAgentId = row.agentId
			this.currentType = row.accountType
		},
		formatBalance(balance, accountType) {
			if (accountType === '3') {
				// '点数账户'
				return this.formatCurrency(balance)
			} else {
				// '信用账户'
				return '∞'
			}
		}
	}
}
