import list from '@/mixins/list'
import { mapGetters } from 'vuex'
import dayjs from 'dayjs'
import { routerNames } from '@/utils/consts'
import SelectAgentTree from '@/components/SelectAgentTree'
import router from '@/router'
import downloadSure from '@/utils/common/downloadSure'
import Cookies from 'js-cookie'

export default {
	name: routerNames.membersaccounttransaction,
	components: { SelectAgentTree },
	mixins: [list],
	data() {
		const end = dayjs().endOf('day')
		const start = dayjs().startOf('day')

		return {
			hadSearch: false,
			loading: false,
			checkAll: true,
			isIndeterminate: false,
			form: {
				playerId: '',
				reasonKey: '',
				playerLoginName: '',
				nickName: '',
				agentName: '',
				bizType: -1,
				isEqLoginName: 0,
				time: [+start, +end],
				tranType:
					this.$store.state.user.globalDics &&
					this.$store.state.user.globalDics.trantype.map((val) => val.value),
				agentId: '',
				minAmount: '',
				maxAmount: ''
			},
			amountData: {},
			prevIndex: 1,
			summary: '',
			hasNode: false
		}
	},
	computed: {
		...mapGetters(['globalDics']),
		bizTypeList() {
			return [
				{ name: this.$t('a_31'), value: 2 },
				{
					name: this.$t('a_32'),
					value: 4
				}
			]
		},
		trantype() {
			const types = this.globalDics.trantype || []
			return types.map((item) => {
				return {
					value: item.value,
					label: item.text,
					zh: item.text,
					en: item.englishText
				}
			})
		}
	},
	activated() {
		window.clearInterval(window.bbbtimer)
		window.bbbtimer = null
		if (!window.aaatimer) {
			window.aaatimer = window.setInterval(() => {
				this.summary && this.addTotalAmount()
			}, 800)
		}
	},
	destroyed() {
		window.clearInterval(window.aaatimer)
		window.aaatimer = null
	},
	beforeRouteLeave(to, from, next) {
		window.clearInterval(window.aaatimer)
		window.aaatimer = null
		next()
	},
	methods: {
		// 失去焦点的时候重置
		onTimeBlur() {
			this.minDate = null
		},
		loadData() {
			console.log('333')
			if (this.form.time[0] < 1556640000000) {
				this.$message({
					message: this.$t('timeerror'),
					type: 'error'
				})
				return
			}
			this.loading = true
			if (localStorage.getItem('playerIdA')) {
				this.form.playerLoginName = localStorage.getItem('playerLoginNameA')
				this.form.playerId = localStorage.getItem('playerIdA')
				this.form.isEqLoginName = 1
				localStorage.removeItem('playerLoginNameA')
				localStorage.removeItem('playerIdA')
			}
			const initData = {
				pageSize: this.pageSize,
				pageIndex: this.pageIndex,
				startTransactionTime: this.form.time[0],
				endTransactionTime: this.form.time[1],
				playerLoginName: this.form.playerLoginName,
				tranType: this.form.tranType.join(),
				reasonKey: this.form.reasonKey,
				minAmount: this.form.minAmount,
				isEqLoginName: this.form.isEqLoginName,
				id: this.form.id,
				bizType: this.form.bizType,
				playerId: this.form.playerId,
				maxAmount: this.form.maxAmount
			}
			const requestData = this.hadSearch
				? {
						nickName: this.form.nickName,
						searchAgentId: this.form.agentId
				  }
				: {}
			const form = {
				...initData,
				...requestData
			}
			this.$api
				.memberQueryAccountRecord(form)
				.then((_) => {
					this.loading = false
					const data = _.data
					this.list = data.record
					this.total = data.totalRecord
					this.summary = data.summary

					this.$nextTick(() => {
						this.addTotalAmount()
					})
				})
				.catch(() => {
					this.pageIndex = this.prevIndex
					this.list = []
					this.total = 0
					this.loading = false
				})
		},
		addTotalAmount() {
			const totalSpan = document.querySelector('.total-span')
			const pageNode = document.querySelector('.el-pagination')
			const prevNode = document.querySelector('.el-pagination__total')
			const newNode = document.createElement('span')
			if (!totalSpan) {
				newNode.innerText = this.$t('members_accounttransaction_index_506', [
					this.summary
				])
				newNode.style.margin = '0 15px'
				newNode.style.fontWeight = '400'
				newNode.className = 'total-span'
				pageNode && pageNode.insertBefore(newNode, prevNode)
			} else {
				const str = this.$t('members_accounttransaction_index_506', [this.summary])
				totalSpan.innerText = str
			}
		},
		filterBiz(value) {
			let name = ''
			this.bizTypeList.forEach((item) => {
				if (item.value === value) {
					name = item.name
				}
			})
			return name
		},
		handleCurrentChange(val) {
			this.prevIndex = this.pageIndex
			this.pageIndex = val
			this.loadData()
		},
		handleCheckedTypesChange(value) {
			const checkedCount = value.length
			this.checkAll = checkedCount === this.trantype.length
		},
		handleCheckAllChange(val) {
			const checkedArr = val ? [...this.trantype.map((item) => item.value)] : []
			this.form.tranType = checkedArr
			this.isIndeterminate = false
		},
		filterTranType(c) {
			const arr = this.trantype || []
			const find = arr.find((item) => Number(item.value) === Number(c))
			if (Cookies.get('language') === 'zh') {
				return (find && find.zh) || '-'
			}
			if (Cookies.get('language') === 'en') {
				return (find && find.en) || '-'
			}
			return '-'
		},
		handleReset() {
			if (this.$route.query && JSON.stringify(this.$route.query) !== '{}') {
				router.push(this.$route.path, () => {
					this.resetCommon()
				})
			} else {
				this.resetCommon()
			}
		},
		resetCommon() {
			const end = dayjs().endOf('day')
			const start = dayjs().startOf('day')

			this.isIndeterminate = false
			this.checkAll = true
			this.hadSearch = false
			this.form = {
				reasonKey: '',
				nickName: '',
				playerId: '',
				playerLoginName: '',
				agentName: '',
				time: [+start, +end],
				minAmount: '',
				bizType: -1,
				isEqLoginName: 0,
				maxAmount: '',
				tranType:
					this.$store.state.user.globalDics &&
					this.$store.state.user.globalDics.trantype.map((val) => val.value)
			}
			this.initList()
		},
		handleSearch() {
			this.hadSearch = true
			this.pageIndex = 1
			this.loadData()
		},
		exportCsv() {
			const params = {
				startTransactionTime: this.form.time[0],
				endTransactionTime: this.form.time[1],
				playerLoginName: this.form.playerLoginName,
				nickName: this.form.nickName,
				searchAgentId: this.form.agentId,
				tranType: this.form.tranType.join()
			}
			downloadSure(
				this.$api,
				'queryAccountRecordCSV',
				params,
				this.$t('members_accounttransaction_index_507')
			)
		}
	}
}
