import list from '@/mixins/list'
import dayjs from 'dayjs'
import { mapGetters } from 'vuex'
import Video from '@/components/Video'
import { routerNames } from '@/utils/consts'
import ResultShow from '@/components/ResultShow'
import Cookies from 'js-cookie'

export default {
	name: routerNames.reportGameResult,
	components: { Video, ResultShow },
	mixins: [list],
	data() {
		const start = dayjs()
			.startOf('day')
			.valueOf()
		const end = dayjs()
			.endOf('day')
			.valueOf()
		return {
			hadSearch: false, // 判断是否点击，解决分页不点搜索带上搜索条件的问题
			loading: false,
			drawer: false,
			dialogShow: false,
			form: {
				time: [start, end],
				operateStatus: -1,
				physicsTableNo: '',
				roundNo: '',
				roundStatus: -1,
				gameTypeId: -1
			},
			list: [],
			videoSrc: '',
			showVideo: false,
			currentData: {}
		}
	},
	computed: {
		...mapGetters(['globalDics']),
		gametype() {
			const types = this.globalDics.gametype || []
			return types.map((item) => {
				return {
					value: item.value,
					label: item.text,
					zh: item.text,
					en: item.englishText
				}
			})
		},
		gamemode() {
			return this.globalDics.gamemode
		},
		gameresultstatus() {
			const types = this.globalDics.gameresultstatus || []
			return types.map((item) => {
				return {
					value: item.value,
					label: item.text,
					zh: item.text,
					en: item.englishText
				}
			})
		},
		betstatus() {
			return this.globalDics.betstatus
		},
		betflag() {
			const types = this.globalDics.betflag || []
			return types.map((item) => {
				return {
					value: item.value,
					label: item.text,
					zh: item.text,
					en: item.englishText
				}
			})
		},
		pickerOptions() {
			const _this = this
			return {
				disabledDate(time) {
					const now = dayjs()
						.endOf('day')
						.valueOf()

					if (_this.minDate) {
						const before = dayjs(_this.minDate)
							.startOf('day')
							.subtract(6, 'd')
							.valueOf()
						const after = dayjs(_this.minDate)
							.endOf('day')
							.add(6, 'd')
							.valueOf()
						return (
							time.getTime() > now || time.getTime() < before || time.getTime() > after
						)
					}
					return time.getTime() > now
				},
				onPick({ maxDate, minDate }) {
					if (!maxDate) {
						_this.minDate = minDate
					}
				},
				shortcuts: [
					{
						text: this.$t('gamemanagement_gameresult_index_372'),
						onClick(picker) {
							const end = dayjs().endOf('day')
							const start = dayjs().startOf('day')
							picker.$emit('pick', [+start, +end])
						}
					},
					{
						text: this.$t('gamemanagement_gameresult_index_373'),
						onClick(picker) {
							const end = dayjs()
								.endOf('day')
								.subtract(1, 'd')
							const start = dayjs()
								.startOf('day')
								.subtract(1, 'd')
							picker.$emit('pick', [+start, +end])
						}
					},
					{
						text: this.$t('gamemanagement_gameresult_index_374'),
						onClick(picker) {
							let start, end
							const weekNum = dayjs().day()
							if (weekNum === 0) {
								end = new Date()
								start = dayjs()
									.subtract(1, 'd')
									.startOf('week')
									.startOf('day')
									.add(1, 'd')
							} else {
								end = new Date()
								start = dayjs()
									.startOf('day')
									.startOf('week')
									.add(1, 'd')
							}
							picker.$emit('pick', [+start, +end])
						}
					}
				]
			}
		}
	},
	methods: {
		// 失去焦点的时候重置
		onTimeBlur() {
			this.minDate = null
		},
		loadData() {
			this.list = [
				{
					playerLoginName: 'ZB1234',
					nickName: 'XXX',
					gameTypeName: this.$t('beautygirlrecord_index_16'),
					physicsTableNo: 'B22',
					settlementTime: '2019-10-25 10:59:21',
					transactionNo: 'YBZR201910250000000001',
					girlAccount: 'ZB1234',
					girlName: 'ZB1234',
					tranType: this.$t('beautygirlrecord_index_17'),
					preBalance: '23333333',
					amount: '122222',
					afterBalance: '666666',
					transactionTime: '2019-10-25 10:59:21',
					remark: this.$t('beautygirlrecord_index_18'),
					roundNo: 'G101119A2401B',
					result: this.$t('beautygirlrecord_index_19'),
					money1: '2200',
					money2: '2200',
					money3: '2200',
					money4: '2200',
					game: this.$t('beautygirlrecord_index_20')
				}
			]

			return false

			// this.loading = true

			// const pageData = {
			// 	pageSize: this.pageSize,
			// 	pageIndex: this.pageIndex,
			// 	startTime: this.form.time ? this.form.time[0] : '',
			// 	endTime: this.form.time ? this.form.time[1] : ''
			// }
			// const requestData = this.hadSearch
			// 	? {
			// 			roundStatus: this.form.roundStatus,
			// 			operateStatus: this.form.operateStatus,
			// 			bootNo: this.form.bootNo,
			// 			physicsTableNo: this.form.physicsTableNo,
			// 			roundNo: this.form.roundNo,
			// 			gameTypeId: this.form.gameTypeId
			// 	  }
			// 	: {}
			// const form = {
			// 	...pageData,
			// 	...requestData
			// }
			// console.log('form :', form)
			// this.$api
			// 	.gameResultSearch(form)
			// 	.then((_) => {
			// 		this.loading = false
			// 		const data = _.data
			// 		this.list = data.record
			// 		this.total = data.totalRecord
			// 	})
			// 	.catch(() => {
			// 		this.loading = false
			// 	})
		},
		filterList(list) {
			return list.map((_) => {
				if (_.status === 1) {
					_.switchStatus = true
				} else if (_.status === 0) {
					_.switchStatus = false
				}
				return _
			})
		},
		handleCurrentChange(val) {
			console.log(this.$t('detailrecord.detailrecord_32', [val]))
			this.loadData()
		},
		handleReset() {
			const start = dayjs()
				.startOf('day')
				.valueOf()
			const end = dayjs()
				.endOf('day')
				.valueOf()
			this.form = {
				time: [start, end],
				physicsTableNo: '',
				roundId: '',
				roundStatus: -1,
				gameTypeId: -1,
				operateStatus: -1
			}
			this.hadSearch = false
			this.initList()
		},
		handleSearch() {
			this.hadSearch = true
			this.pageIndex = 1
			this.loadData()
		},
		handleShowVideo(path, row) {
			this.showVideo = true
			this.videoSrc = 'http://clips.vorwaerts-gmbh.de/big_buck_bunny.mp4'
			this.currentData = { ...row }
		},

		showResult(row) {
			this.dialogShow = true
			this.currentData = { ...row }
		},
		clearSrc() {
			this.videoSrc = ''
		},
		filterGametype(c) {
			const arr = this.gametype || []
			const find = arr.find((item) => item.label === c)
			if (Cookies.get('language') === 'zh') {
				return (find && find.zh) || '-'
			}
			if (Cookies.get('language') === 'en') {
				return (find && find.en) || '-'
			}
			return '-'
		},
		filterGameresultstatus(c) {
			const arr = this.gameresultstatus || []
			const find = arr.find((item) => Number(item.value) === Number(c))
			if (Cookies.get('language') === 'zh') {
				return (find && find.zh) || '-'
			}
			if (Cookies.get('language') === 'en') {
				return (find && find.en) || '-'
			}
			return '-'
		},
		filterBetflag(c) {
			const arr = this.betflag || []
			const find = arr.find((item) => Number(item.value) === Number(c))
			if (Cookies.get('language') === 'zh') {
				return (find && find.zh) || '-'
			}
			if (Cookies.get('language') === 'en') {
				return (find && find.en) || '-'
			}
			return '-'
		}
	}
}
