import ResultShow from '@/components/ResultShow'
import { mapGetters } from 'vuex'
import { formatCurrency } from '@/utils'
import Cookies from 'js-cookie'

export default {
	name: 'BetDetail',
	components: { ResultShow },
	props: {
		detailVisible: {
			type: Boolean,
			default: false
		},
		currentData: {
			type: Object,
			default: () => {}
		}
	},
	data() {
		return {
			form: {
				limitRed: -1,
				accountStatus: 1,
				ifBet: 1,
				mostHigh: 0
			},
			recordList: [
				{
					value: 0,
					label: this.$t('abnormalround_players_components_historyrecord_96')
				},
				{
					value: 1,
					label: this.$t('abnormalround_players_components_historyrecord_97')
				},
				{
					value: 2,
					label: this.$t('abnormalround_players_components_historyrecord_98')
				},
				{
					value: 3,
					label: this.$t('abnormalround_players_components_historyrecord_99')
				},
				{
					value: 4,
					label: this.$t('agent_24')
				}
			],
			deviceTypeList: [
				{
					value: 1,
					label: 'PC'
				},
				{
					value: 2,
					label: 'H5'
				},
				{
					value: 3,
					label: 'APP'
				},
				{
					value: 4,
					label: 'APP'
				},
				{
					value: 5,
					label: this.$t('new_69')
				}
			],
			detailData: {},
			visible: false
		}
	},
	computed: {
		...mapGetters(['userInfo']),
		...mapGetters(['globalDics']),
		gamemode() {
			const types = this.globalDics.gamemode || []
			return types.map((item) => {
				return {
					value: item.value,
					label: item.text,
					zh: item.text,
					en: item.englishText
				}
			})
		},
		betflag() {
			const types = this.globalDics.betflag || []
			return types.map((item) => {
				return {
					value: item.value,
					label: item.text,
					zh: item.text,
					en: item.englishText
				}
			})
		},
		gametype() {
			const types = this.globalDics.gametype || []
			return types.map((item) => {
				return {
					value: item.value,
					label: item.text,
					zh: item.text,
					en: item.englishText
				}
			})
		}
	},
	watch: {
		detailVisible(val) {
			this.visible = val
		},
		currentData(val) {
			this.detailData = val
		}
	},
	methods: {
		formatCurrency,
		getAmoutColor(value) {
			if (value > 0) {
				return 'green'
			} else if (value < 0) {
				return 'red'
			}
			return ''
		},
		cancel() {
			this.$emit('update:detailVisible', false)
		},
		deviceTypeFilter(value) {
			let label = ''
			this.deviceTypeList.forEach((item) => {
				if (Number(item.value) === Number(value)) {
					label = item.label
				}
			})
			return label
		},
		recordTypeFilter(value) {
			let label = ''
			this.recordList.forEach((item) => {
				if (Number(item.value) === Number(value)) {
					label = item.label
				}
			})
			return label
		},
		filterGameMode(c) {
			const arr = this.gamemode || []
			const find = arr.find((item) => Number(item.value) === Number(c))
			if (Cookies.get('language') === 'zh') {
				return (find && find.zh) || '-'
			}
			if (Cookies.get('language') === 'en') {
				return (find && find.en) || '-'
			}
			return '-'
		},
		filterBetflag(c) {
			const arr = this.betflag || []
			const find = arr.find((item) => Number(item.value) === Number(c))
			if (Cookies.get('language') === 'zh') {
				return (find && find.zh) || '-'
			}
			if (Cookies.get('language') === 'en') {
				return (find && find.en) || '-'
			}
			return '-'
		},
		filterRoom(value) {
			const val =
				value === 1
					? this.$t('members_list_index_682')
					: this.$t('members_list_index_683')
			return val
		},
		filterGametype(c) {
			const arr = this.gametype || []
			const find = arr.find((item) => item.label === c)
			if (Cookies.get('language') === 'zh') {
				return (find && find.zh) || '-'
			}
			if (Cookies.get('language') === 'en') {
				return (find && find.en) || '-'
			}
			return '-'
		}
	}
}
