import { setPageTitle } from '@/utils/i18n'

export default {
	computed: {
		language() {
			return this.$store.getters.language
		}
	},
	methods: {
		handleSetLanguage(lang) {
			this.$i18n.locale = lang
			this.$store.dispatch('app/setLanguage', lang)
			setPageTitle()

			if (lang === 'zh') {
				this.$message({
					message: '语言切换成功',
					type: 'success'
				})
			} else {
				this.$message({
					message: 'Switch Language Success',
					type: 'success'
				})
			}
		}
	}
}
