import { mapGetters } from 'vuex'
import list from '@/mixins/list'
import { colorNum } from '@/utils'
import dayjs from 'dayjs'
import { routerNames } from '@/utils/consts'
import SelectAgentTree3 from '@/components/SelectAgentTree3'
import router from '@/router'

export default {
	name: routerNames.reportDaily,
	components: { SelectAgentTree3 },
	mixins: [list],
	data() {
		const end = dayjs()
			.endOf('day')
			.subtract(1, 'd')
			.valueOf()
		const start = dayjs()
			.startOf('day')
			.subtract(7, 'd')
			.valueOf()
		return {
			time: [start, end],
			hadSearch: false,
			canNoticeTotal: true,
			canNoticeTime: true,
			loading: false,
			canDownload: true,
			amountData: {},
			form: {
				time: '',
				agentId: '',
				playerLoginName: ''
			},
			first: true,
			summary: {}
		}
	},
	computed: {
		...mapGetters(['name', 'userInfo'])
	},
	mounted() {},
	destroyed() {},
	methods: {
		loadData() {
			this.loading = true
			if (this.first) {
				const query = {}
				query.agentId = localStorage.getItem('agentId')
				query.playerLoginName = localStorage.getItem('playerLoginName')
				query.time = localStorage.getItem('time')
				this.form = {
					agentId: query.agentId,
					playerLoginName: query.playerLoginName,
					time: JSON.parse(query.time)
				}
			}
			this.first = false
			router.push(this.$route.path)
			this.$nextTick(() => {
				this.timestamp = Date.now()
			})

			const initData = {
				pageIndex: this.pageIndex,
				pageSize: this.pageSize,
				startReportTime: this.form.time ? this.form.time[0] : '',
				endReportTime: this.form.time ? this.form.time[1] : '',
				agentId: this.form.agentId,
				playerLoginName: this.form.playerLoginName
			}

			this.$api
				.dailyDetails(initData)
				.then((_) => {
					this.loading = false
					if (_.code === 200) {
						const data = _.data
						this.list = data.record || []
						this.total = data.totalRecord
						this.summary = data.summary
					}
				})
				.catch(() => {
					this.loading = false
				})
		},
		getSummaries(param) {
			const { columns, data } = param
			const sums = []
			columns.forEach((column, index) => {
				if (index === 0) {
					sums[index] = this.$t('members_report_index_723')
					return
				}
				const values = data.map((item) => Number(item[column.property]))
				if (
					!values.every((value) => isNaN(value)) &&
					(column.property === 'betCount' ||
						column.property === 'betAmount' ||
						column.property === 'netAmount' ||
						column.property === 'cumulativePayout' ||
						column.property === 'payoutAmount' ||
						column.property === 'activityAmount' ||
						column.property === 'gainAmount' ||
						column.property === 'validBetAmount')
				) {
					sums[index] = this.summary[column.property]
				}
			})
			return sums
		},

		colorNum(val) {
			return colorNum(val)
		},
		handleCurrentChange(val) {
			this.loadData()
		},
		handleReset() {
			const end = dayjs()
				.subtract(1, 'd')
				.endOf('day')
				.valueOf()
			const start = dayjs()
				.startOf('day')
				.subtract(7, 'd')
				.valueOf()
			this.form.time = [start, end]
			this.hadSearch = false
			this.initList()
		},
		handleSearch() {
			this.hadSearch = true
			this.pageIndex = 1
			this.loadData()
		},
		exportCsv() {
			const params = {
				startReportTime: this.form.time ? this.form.time[0] : '',
				endReportTime: this.form.time ? this.form.time[1] : '',
				agentId: this.form.agentId,
				playerLoginName: this.form.playerLoginName
			}
			if (Number(this.total) > 20000) {
				if (this.canNoticeTotal) {
					this.$message({
						message: this.$t('new_2'),
						type: 'error'
					})
					this.canNoticeTotal = false
					setTimeout(() => {
						this.canNoticeTotal = true
					}, 3000)
				}
				return
			}
			if (!this.canDownload) {
				if (this.canNoticeTime) {
					this.$message({
						message: this.$t('new_1'),
						type: 'error'
					})
					this.canNoticeTime = false
					setTimeout(() => {
						this.canNoticeTime = true
					}, 3000)
				}
				return
			}
			this.canDownload = false
			setTimeout(() => {
				this.canDownload = true
			}, 60000)
			const startday = dayjs(params.startReportTime).get('date')
			const startmonth = dayjs(params.startReportTime).get('month') + 1
			const endmonth = dayjs(params.endReportTime).get('month') + 1
			const endday = dayjs(params.endReportTime).get('date')
			const title =
				this.$t('route.reportDaily') +
				'(' +
				startmonth +
				this.$t('merchant_dailyrateaccount_index_802') +
				startday +
				this.$t('merchant_dailyrateaccount_index_803') +
				'-' +
				endmonth +
				this.$t('merchant_dailyrateaccount_index_802') +
				endday +
				this.$t('merchant_dailyrateaccount_index_803') +
				')'
			localStorage.setItem('title', title)
			this.$api
				.dailyDetailsExport(params)
				.then((res) => {})
				.catch(() => {
					this.loading = false
				})
		},

		goPrev() {
			router.push(`/members/report`)
		}
	}
}
