import list from '@/mixins/list'
import { mapGetters } from 'vuex'
import { routerNames } from '@/utils/consts'
import SelectAgentTree from '@/components/SelectAgentTree'
import { message } from '@/utils/message'
import Cookies from 'js-cookie'
import dayjs from 'dayjs'
export default {
	name: routerNames.ipwhite,
	components: { SelectAgentTree },
	mixins: [list],
	data() {
		return {
			hadSearch: false,
			loading: false,
			ipDialogVisible: false,
			activeName: '0',
			form: {
				ip: '',
				time: '',
				status: -1,
				type: -1,
				agentId: '',
				updatedBy: '',
				quickSearch: ''
			},
			ipDialogForm: {
				type: '',
				ip: '',
				remark: '',
				agentId: '',
				updatedBy: '',
				agentName: ''
			}
		}
	},

	computed: {
		...mapGetters(['buttonPermissions']),
		...mapGetters(['globalDics']),
		pickerOptions() {
			const _this = this
			return {
				disabledDate(time) {
					const now = dayjs()
						.endOf('day')
						.valueOf()
					return time.getTime() > now
				},
				onPick({ maxDate, minDate }) {
					if (!maxDate) {
						_this.minDate = minDate
					}
				},
				shortcuts: [
					{
						text: this.$t(`members_betrecord_index_584`),
						onClick(picker) {
							const end = dayjs().endOf('day')
							const start = dayjs().startOf('day')
							picker.$emit('pick', [+start, +end])
						}
					},
					{
						text: this.$t(`members_betrecord_index_585`),
						onClick(picker) {
							const end = dayjs()
								.endOf('day')
								.subtract(1, 'd')
							const start = dayjs()
								.startOf('day')
								.subtract(1, 'd')
							picker.$emit('pick', [+start, +end])
						}
					},
					{
						text: this.$t(`members_betrecord_index_586`),
						onClick(picker) {
							let start, end
							const weekNum = dayjs().day()
							if (weekNum === 0) {
								end = dayjs().endOf('day')
								start = dayjs()
									.subtract(1, 'd')
									.startOf('week')
									.startOf('day')
									.add(1, 'd')
							} else {
								end = dayjs().endOf('day')
								start = dayjs()
									.startOf('day')
									.startOf('week')
									.add(1, 'd')
							}
							picker.$emit('pick', [+start, +end])
						}
					},
					{
						text: this.$t(`members_betrecord_index_587`),
						onClick(picker) {
							const end = dayjs().endOf('day')
							const start = dayjs().startOf('month')
							picker.$emit('pick', [+start, +end])
						}
					},
					{
						text: this.$t(`merchant_dailyrateaccount_index_799`),
						onClick(picker) {
							const end = dayjs().endOf('day')
							const start = dayjs()
								.startOf('day')
								.subtract(89, 'd')
							picker.$emit('pick', [+start, +end])
						}
					}
				]
			}
		},
		whiteiptype() {
			const types = this.globalDics.whiteiptype || []
			return types.map((item) => {
				return {
					value: item.value,
					label: item.text,
					zh: item.text,
					en: item.englishText
				}
			})
		},
		options() {
			return [
				{
					value: 'self',
					label: this.$t('systemmanagement_ipwhite_index_1458')
				},
				{
					value: 'under',
					label: this.$t('systemmanagement_ipwhite_index_1459')
				},
				{
					value: 'team',
					label: this.$t('systemmanagement_ipwhite_index_1460')
				}
			]
		},
		ipDialogRules() {
			// const validateIp = (rule, value, callback) => {
			// 	if (
			// 		value &&
			// 		!/^((25[0-5]|2[0-4]\d|((1\d{2})|([1-9]?\d)))\.){3}(25[0-5]|2[0-4]\d|((1\d{2})|([1-9]?\d))|\*)((\/([012]\d|3[012]|\d))?)(,((25[0-5]|2[0-4]\d|((1\d{2})|([1-9]?\d)))\.){3}(25[0-5]|2[0-4]\d|((1\d{2})|([1-9]?\d))|\*)((\/([012]\d|3[012]|\d))?))*$/.test(
			// 			value
			// 		)
			// 	) {
			// 		callback(new Error('IP地址不符合规范，请重新输入'))
			// 	} else {
			// 		callback()
			// 	}
			// }
			return {
				agentName: [
					{
						required: true,
						message: this.$t('systemmanagement.l_systemmanagement_146'),
						trigger: 'blur'
					}
				],
				type: [
					{
						required: true,
						message: this.$t('systemmanagement.systemmanagement_68'),
						trigger: 'blur'
					}
				],
				ip: [
					{
						required: true,
						message: this.$t('systemmanagement_ipwhite_index_1453'),
						trigger: 'blur'
					}
					// { validator: validateIp, trigger: 'blur' }
				],
				remark: [
					{
						message: this.$t('systemmanagement.systemmanagement_70'),
						trigger: 'blur'
					}
				],
				updatedBy: [
					{
						required: true,
						message: this.$t('new_81'),
						trigger: 'blur'
					}
				]
			}
		}
		// ipBatchDialogRules() {
		// 	return {
		// 		file: [
		// 			{
		// 				required: true,
		// 				message: this.$t('systemmanagement.systemmanagement_71'),
		// 				trigger: 'blur'
		// 			}
		// 		],
		// 		agentName: [
		// 			{
		// 				required: true,
		// 				message: this.$t('systemmanagement.l_systemmanagement_146'),
		// 				trigger: 'blur'
		// 			}
		// 		],
		// 		remark: [
		// 			{
		// 				message: this.$t('systemmanagement.systemmanagement_70'),
		// 				trigger: 'blur'
		// 			}
		// 		]
		// 	}
		// }
	},
	watch: {
		'form.quickSearch'(val) {
			console.log('val :>> ', val)
			// this.loadData()
		}
	},
	methods: {
		loadData() {
			this.loading = true
			const initData = {
				pageSize: this.pageSize,
				pageIndex: this.pageIndex,
				startDate: this.form.time ? this.form.time[0] : '',
				endDate: this.form.time ? this.form.time[1] : '',
				quickSearch: this.form.quickSearch,
				updatedBy: this.form.updatedBy
			}
			const requestData = this.hadSearch
				? {
						ip: this.form.ip,
						agentId: this.form.agentId,
						type: this.form.type,
						status: this.form.status
				  }
				: {}
			const form = {
				...initData,
				...requestData
			}
			this.$api
				.systemIpwhitelist({
					...form
				})
				.then((_) => {
					this.loading = false
					const data = _.data
					this.total = data.totalRecord
					this.list = data.record
				})
				.catch(() => {
					this.loading = false
				})
		},
		loadData2() {
			this.loading = true
			const initData = {
				type: this.form.type,
				agentId: this.form.agentId,
				pageIndex: this.pageIndex,
				pageSize: this.pageSize
			}
			const form = {
				...initData
			}
			this.$api
				.polymIpwhitelist({
					...form
				})
				.then((_) => {
					this.loading = false
					const data = _.data
					this.total = data.totalRecord
					this.list = data.record
				})
				.catch(() => {
					this.loading = false
				})
		},
		clickTab() {
			this.list = []
			this.pageIndex = 1
			if (this.activeName === '0') {
				this.loadData()
			} else {
				this.loadData2()
			}
		},
		handleReset() {
			this.form = {
				time: '',
				ip: '',
				updatedBy: '',
				type: -1,
				status: -1,
				quickSearch: ''
			}
			this.hadSearch = false
			this.initList()
		},
		handleCreateWhite() {
			this.ipDialogVisible = true
			this.ipDialogForm = {
				type: 1,
				agentId: ''
			}
		},
		editRow(row) {
			this.ipDialogVisible = true
			this.ipDialogForm = { ...row }
		},
		filterIps(list) {
			console.log('list :', list)
			const ipArr = []
			list.forEach((item) => {
				// if(item.)
				ipArr.push(item.ip)
			})
			return ipArr
		},
		filterCheckedList(list) {
			console.log('list :', list)
			const idArr = []
			list.forEach((item) => {
				// if(item.)
				idArr.push(item.id)
			})
			return idArr
		},
		handleSearch() {
			this.hadSearch = true
			this.pageIndex = 1
			this.list = []
			if (this.activeName === '0') {
				this.loadData()
			} else {
				this.loadData2()
			}
		},
		commitIpDialog() {
			this.$refs.ipDialogForm.validate((val) => {
				if (val) {
					const loading = this.$loading({
				lock: true,
				text: 'Loading',
				spinner: 'el-icon-loading',
				background: 'rgba(0, 0, 0, 0.7)'
			})
					if (!this.ipDialogForm.agentId) {
						this.$message.error(this.$t('systemmanagement.systemmanagement_74'))
						return false
					}
					const form = {
						agentId: this.ipDialogForm.agentId || '',
						type: this.ipDialogForm.type,
						remark: this.ipDialogForm.remark,
						updatedBy: this.ipDialogForm.updatedBy,
						ip: this.ipDialogForm.ip,
						id: this.ipDialogForm.id
					}
					const api = this.ipDialogForm.id ? 'systemUpdateIpWhite' : 'systemCreate'
					const text = this.ipDialogForm.id
						? this.$t('systemmanagement_ipwhite_index_1461')
						: this.$t('systemmanagement_ipwhite_index_1462')
					this.$api[api](form).then(() => {
						this.ipDialogVisible = false
						this.$message({
							type: 'success',
							message: this.$t('systemmanagement_ipwhite_index_1463', [
								this.fitlerIpScope(form.type),
								text
							])
						})
						this.loadData()
						loading.close()
					}).catch(() => {
						loading.close()
					})
				}
			})
		},
		querySearchAgentName(keywords, cb) {
			this.$api.systemSearchAgents({ keywords }).then((_) => {
				if (_.code === 200) {
					clearTimeout(this.timeout)
					this.timeout = setTimeout(() => {
						cb(_.data)
					}, 500)
				}
			})
		},
		fitlerIpScope(c) {
			const arr = this.whiteiptype || []
			const find = arr.find((item) => Number(item.value) === Number(c))
			if (Cookies.get('language') === 'zh') {
				return (find && find.zh) || '-'
			}
			if (Cookies.get('language') === 'en') {
				return (find && find.en) || '-'
			}
			return '-'
		},
		handleSizeChange(value) {
			this.pageSize = value
			if (this.activeName === '0') {
				this.loadData()
			} else {
				this.loadData2()
			}
		},
		handleCurrentChange(val) {
			this.prevIndex = this.pageIndex
			this.pageIndex = val
			if (this.activeName === '0') {
				this.loadData()
			} else {
				this.loadData2()
			}
		},
		switchChange(val, row) {
			const params = {
				id: row.id,
				status: val ? '1' : '0'
			}
			this.$api.systemUpdateIpWhiteStatus(params).then((res) => {
				if (res.code === 200) {
					const index = this.list.findIndex((val) => val.id === row.id)
					this.list[index].status = params.status
					const statusText =
						params.status === '1'
							? this.$t('systemmanagement_ipwhite_index_1438')
							: this.$t('systemmanagement_ipwhite_index_1439')
					return message({
						message: this.$t('systemmanagement_ipwhite_index_1464', [statusText]),
						type: 'success'
					})
				}
			})
		},
		onCancel() {
			this.ipDialogVisible = false
			this.$refs.ipDialogForm.clearValidate()
		}
	}
}
