import list from '@/mixins/list'
import LimitPlan from './component/LimitPlan'
import { mapGetters } from 'vuex'
import { routerNames } from '@/utils/consts'

export default {
	name: routerNames.agentsLimitRed,
	components: {
		LimitPlan
	},
	mixins: [list],
	data() {
		return {
			drawer: false,
			limitPlanShow: false,
			limitStatus: 1,
			editFlag: 0,
			form: {
				id: ''
			},
			limitDialogVisible: false,
			EditLimitForm: {},
			limitId: '',
			limitPlanData: {
				id: '',
				type: ''
			}
		}
	},
	computed: {
		...mapGetters(['buttonPermissions']),
		rules() {
			const validName = (rule, value, callback) => {
				const regValue = value ? value.replace(/[\u4e00-\u9fa5]/g, 'xx') : ''
				if (regValue && /^[A-Za-z0-9]{4,20}$/.test(regValue)) {
					callback()
				} else {
					callback(new Error(this.$t('message.deskValidName')))
				}
			}
			const validateNumber = (rule, value, callback) => {
				const EditLimitForm = this.EditLimitForm
				if (!/^[0-9]*[1-9][0-9]*$/.test(value)) {
					callback(new Error(this.$t('message.integerThan0')))
				} else if (
					rule.field === 'max' &&
					EditLimitForm.min &&
					Number(EditLimitForm.min) > Number(value)
				) {
					callback(new Error(this.$t('message.maxThanMin')))
				} else if (
					rule.field === 'min' &&
					EditLimitForm.max &&
					Number(EditLimitForm.max) < Number(value)
				) {
					callback(new Error(this.$t('message.minThanMax')))
				}
				callback()
			}
			return {
				limitRedName: [{ required: true, validator: validName, trigger: 'blur' }],
				min: [
					{
						required: true,
						message: this.$t('message.minimumBet'),
						trigger: 'blur'
					},
					{
						validator: validateNumber,
						trigger: 'blur'
					}
				],
				max: [
					{
						required: true,
						message: this.$t('message.maximumBet'),
						trigger: 'blur'
					},
					{
						validator: validateNumber,
						trigger: 'blur'
					}
				]
			}
		}
	},
	methods: {
		loadData() {
			this.$api
				.merchantGetLimitRed()
				.then((_) => {
					if (_.code === 200) {
						this.list = _.data
					} else {
						this.list = []
					}
				})
				.catch((_) => {
					this.list = []
				})
		},
		changeLimitStatus(val) {
			this.limitStatus = val
			this.loadData()
		},
		handleCurrentChange(val) {
			console.log(this.$t('merchant.merchant_27', [val]))
			this.loadData()
		},
		handleReset() {
			this.form = {
				id: '',
				time: ''
			}
		},
		handleSearch() {},
		handleNewLimit() {
			this.editFlag = 0
			this.EditLimitForm = {}
			this.limitDialogVisible = true
		},
		handleDetail(item) {
			this.limitPlanShow = true
			this.limitPlanData = { ...item, type: this.limitStatus }
		},
		editRow(index, row, flag) {
			this.limitDialogVisible = true
			this.editFlag = flag
			this.EditLimitForm = row
		},
		stopRow(index, row) {
			const status = row.status ? 0 : 1
			this.$api
				.merchantLimitRedModifyStatus({
					...row,
					status
				})
				.then((_) => {
					if (_.code === 200) {
						this.$message({
							type: 'success',
							message: this.$t('merchant.merchant_28')
						})
						this.loadData()
					}
				})
		},
		cancelLimitDialog() {
			this.limitDialogVisible = false
			this.$refs.form.resetFields()
		},
		commitLimitDialog() {
			const form = this.EditLimitForm
			this.$refs.form.validate((valid) => {
				if (valid) {
					if (this.editFlag) {
						// 修改
						this.$api.merchantLimitRedEdit(form).then((_) => {
							if (_.code === 200) {
								this.limitDialogVisible = false
								this.$message({
									type: 'success',
									message: this.$t('merchant.merchant_28')
								})
							}
						})
					} else {
						this.$api.merchantLimitRedAdd(form).then((_) => {
							if (_.code === 200) {
								this.limitDialogVisible = false
								this.$message({
									type: 'success',
									message: this.$t('merchant.merchant_29')
								})
							}
						})
					}
				}
			})
		}
	}
}
