import { mapGetters } from 'vuex'
import list from '@/mixins/list'
import dayjs from 'dayjs'
import { routerNames } from '@/utils/consts'
import SelectAgentTree from '@/components/SelectAgentTree'

export default {
	name: routerNames.activityRebate,
	components: { SelectAgentTree },
	mixins: [list],
	data() {
		const end = dayjs()
			.endOf('day')
			.valueOf()
		const start = dayjs()
			.startOf('day')
			.valueOf()
		return {
			loading: false,
			form: {
				time: [start, end],
				loginName: '',
				searchAgentId: ''
			},
			summary: {}
		}
	},
	computed: {
		...mapGetters(['name', 'userInfo'])
	},
	methods: {
		loadData() {
			this.loading = true
			const initData = {
				pageIndex: this.pageIndex,
				pageSize: this.pageSize
			}
			const requestData = {
				searchAgentId: this.form.searchAgentId,
				loginName: this.form.loginName,
				startTime: this.form.time ? this.form.time[0] : '',
				endTime: this.form.time ? this.form.time[1] : ''
			}
			const form = {
				...initData,
				...requestData
			}
			this.$api
				.getRebateList(form)
				.then((_) => {
					this.loading = false
					if (_.code === 200) {
						const data = _.data
						this.list = data.record || []
						this.total = data.totalRecord
						this.summary = data.summary
					}
				})
				.catch(() => {
					this.loading = false
				})
		},
		getSummaries(param) {
			const { columns } = param
			const sums = []
			columns.forEach((column, index) => {
				if (index === 0) {
					sums[index] = this.$t('members_report_index_723')
				} else if (index === 1) {
					sums[index] = this.$t('new_15') + this.summary.payOutPlayerCount
				} else if (index === 2) {
					sums[index] = this.$t('new_16') + this.summary.payOutCount
				} else if (index === 3) {
					sums[index] = this.$t('new_17') + this.summary.sumPayOutAmount
				}
			})
			return sums
		},

		handleCurrentChange(val) {
			if (this.loading) {
				return
			}
			this.loadData()
		},
		handleReset() {
			const end = dayjs()
				.endOf('day')
				.valueOf()
			const start = dayjs()
				.startOf('day')
				.valueOf()
			this.form = {
				time: [start, end],
				loginName: '',
				searchAgentId: ''
			}
			this.initList()
		},
		handleSearch() {
			this.pageIndex = 1
			this.loadData()
		},
		showDailyDetail(row) {
			this.$store.dispatch('tagsView/delView', {
				name: routerNames.betrecord
			})
			this.$nextTick(() => {
				const targetUrl = '/members/betrecord'
				this.$router.push(`${targetUrl}`)
				localStorage.setItem('playerId', row.playerId)
				localStorage.setItem('roundNo', row.remark.split('_')[1])
				localStorage.setItem('betPoint', row.remark.split('_')[2])
			})
		}
	}
}
