.navbar {
	height: 50px;
	overflow: hidden;
	position: relative;
	background: #fff;
	box-shadow: 0 1px 4px rgba(0, 21, 41, 0.08);

	.hamburger-container {
		line-height: 46px;
		height: 100%;
		float: left;
		cursor: pointer;
		transition: background 0.3s;
		-webkit-tap-highlight-color: transparent;

		&:hover {
			background: rgba(0, 0, 0, 0.025);
		}
	}

	.breadcrumb-container {
		float: left;
	}

	.right-menu {
		position: relative;
		margin-right: 15px;
		float: right;
		height: 100%;
		line-height: 50px;
		.time {
			margin-right: 20px;
		}
		.el-icon-s-custom {
			line-height: 50px;
			font-size: 20px;
			cursor: pointer;
		}
		.member {
			font-size: 14px;
			margin-right: 10px;
		}
		.el-icon-date {
			line-height: 50px;
			margin-right: 10px;
			font-size: 20px;
		}
		&:focus {
			outline: none;
		}

		.right-menu-item {
			display: inline-block;
			padding: 0 8px;
			margin: 0 8px;
			height: 100%;
			font-size: 18px;
			color: #5a5e66;
			vertical-align: text-bottom;

			&.hover-effect {
				cursor: pointer;
				transition: background 0.3s;

				&:hover {
					background: rgba(0, 0, 0, 0.025);
				}
			}
		}

		.avatar-wrapper {
			margin-right: 15px;
			padding-right: 15px;
			position: relative;
			border-right: 1px solid #eee;

			.user-avatar {
				margin-top: 5px;
				margin-right: 10px;
				cursor: pointer;
				width: 40px;
				height: 40px;
				border-radius: 50%;
			}

			.name {
				font-size: 14px;
				color: #303133;
			}
		}
		.login-out {
			margin-top: 10px;
			width: 40px;
			height: 30px;
			display: inline-block;
			background: url('../../assets/img/bb_bt_logout.png') no-repeat;
			cursor: pointer;
		}
		.login-out:active {
			background: url('../../assets/img/bb_bt_logout_pre.png') no-repeat;
		}
	}
}
.user-wrapper {
	z-index: 99999;
	line-height: 32px;
	padding: 20px;
	width: 55%;
	top: 48px;
	right: 70px;
	position: absolute;
	background: rgba(242, 242, 242, 1);
	h2 {
		margin: 0;
		font-size: 18px;
	}
	span {
		font-size: 13px;
	}
}
.span-label {
	display: inline-block;
	width: 138px;
	text-align: right;
}
.modify-btn {
	float: right;
	margin-top: 20px;
}
.api-download {
	color: blueviolet;
	font-size: 13px;
	cursor: pointer;
}
