import { message } from '@/utils/message'
import { generateTitle } from '@/utils/i18n'
import list from '@/mixins/list'
import { i18nRoute } from '@/utils/i18n'

export default {
	name: 'AuthoritySearch',
	mixins: [list],
	props: {
		// 0为线路商模板配置 1为普通商户模板配置 2为默认角色配置
		type: {
			type: Number,
			default: 0
		},
		drawer: Boolean,
		roledata: {
			type: Object,
			default: function() {
				return {}
			}
		},
		permissions: {
			type: Array,
			default: () => []
		}
	},
	data() {
		return {
			transferKey: 0,
			elDrawer: false,
			checkedAll: false,
			checkedKeys: [],
			ps: [],
			form: {
				roleName: '',
				remark: ''
			},
			direction: 'ttb',
			restaurants: [],
			state2: '',
			list: [],
			permissionId: ''
		}
	},
	computed: {
		defaultProps() {
			return {
				label: (data) => {
					return generateTitle(data.permissionName)
				},
				children: 'children'
			}
		}
	},
	watch: {
		drawer(val) {
			this.elDrawer = val
			this.hasPermission(15308) && this.loadData2()
		},
		roledata: {
			handler(newVal, old) {
				if (newVal.id) {
					this.form = { ...newVal }
				} else {
					this.resetForm()
				}
			},
			deep: true
		},
		permissions: {
			handler(newVal, oldVal) {
				if (newVal) {
					const arr = this.filterPS(newVal)
					this.restaurants = arr.map((i) => {
						return {
							value: i18nRoute(i.permissionName),
							id: i.id
						}
					})
				}
			},
			deep: true
		}
	},
	methods: {
		loadData2() {
			const form = {
				pageSize: this.pageSize,
				pageIndex: this.pageIndex,
				permissionId: this.permissionId,
				IsPage: true
			}
			this.$api.listPermissionAgent(form).then((res) => {
				this.list = res.data.permissionAgentList
				this.total = res.data.totalRecord
			})
		},
		// 点击复选框判断是否全选
		clickIfAllChecked(checkedKeys) {
			const arr = this.filterPS(this.ps)
			if (arr.length === checkedKeys.length) {
				this.checkedAll = true
			} else {
				this.checkedAll = false
			}
		},
		check(data, obj) {
			this.clickIfAllChecked(obj.checkedKeys)
		},
		filterPS(ps) {
			const list = []
			const getList = function(ps) {
				ps.forEach((item) => {
					if (item.children) {
						list.push(item)
						getList(item.children)
					} else {
						list.push(item)
					}
				})
			}
			getList(ps)
			return list
		},
		resetForm() {
			this.form = {}
			this.checkedAll = false
			this.resetTree()
		},
		handleClose() {
			this.state2 = ''
			this.permissionId = ''
			this.resetDialog()
			this.$emit('update:drawer', false)
		},
		resetDialog() {
			this.transferKey = +new Date()
		},
		querySearch(queryString, cb) {
			const restaurants = this.restaurants
			const results = queryString
				? restaurants.filter((restaurant) => {
						return (
							restaurant.value.toLowerCase().indexOf(queryString.toLowerCase()) === 0
						)
				  })
				: restaurants
			// 调用 callback 返回建议列表的数据
			cb(results)
		},
		loadAll() {
			return [
				{
					value: this.$t(
						'authoritymanagement_defaultrole_components_authoritysearch_193'
					),
					address: this.$t(
						'authoritymanagement_defaultrole_components_authoritysearch_194'
					)
				},
				{
					value: this.$t(
						'authoritymanagement_defaultrole_components_authoritysearch_195'
					),
					address: this.$t(
						'authoritymanagement_defaultrole_components_authoritysearch_196'
					)
				}
			]
		},
		handleSelect(item) {
			this.pageIndex = 1
			this.permissionId = item.id
			this.loadData2()
		},
		handleClearable() {
			this.permissionId = ''
			this.loadData2()
		},
		handleCurrentChange(val) {
			this.loadData2()
		},
		deleteRow(index, row) {
			this.$confirm(
				this.$t('authoritymanagement_defaultrole_components_authoritysearch_197'),
				this.$t('authoritymanagement_defaultrole_components_authoritysearch_198'),
				{
					confirmButtonText: this.$t(
						'authoritymanagement_defaultrole_components_authoritysearch_199'
					),
					cancelButtonText: this.$t(
						'authoritymanagement_defaultrole_components_authoritysearch_200'
					),
					type: 'warning',
					center: true
				}
			)
				.then(() => {
					this.$api
						.deletePermissionAgent({
							permissionId: row.permissionId,
							agentCode: row.agentCode
						})
						.then((res) => {
							if (res.code === 200) {
								this.loadData2()
								return message({
									type: 'success',
									message: this.$t(
										'authoritymanagement_defaultrole_components_authoritysearch_201'
									)
								})
							}
						})
				})
				.catch(() => {})
		},
		// 改变列表条数
		handleSizeChange(value) {
			this.pageSize = value
			localStorage.setItem('pageSize', value)
			this.loadData2()
		},
		getNameByTran(row) {
			return i18nRoute(row.permissionName)
		}
	}
}
