.rotate-right {
	transform: rotate(90deg);
	margin-right: 3.5%;
}

.rotate-left {
	transform: rotate(90deg);
	margin-left: 3.5%;
}

.light-border {
	border: 3px solid yellow;
	border-radius: 10px;
	transform: translateY(3px);
}

.light-border-rotato {
	border: 3px solid yellow;
	border-radius: 10px;
	transform: rotate(90deg) translateY(3px);
}

.cards {
	margin: 0 auto;
	width: 60%;
	.card-wrapper {
		display: flex;
		flex-direction: row;
		justify-content: space-around;
		align-items: center;
	}
	.player {
		color: rgba(22, 22, 250, 1);
	}
	.banker {
		color: rgba(187, 19, 45, 1);
	}
	.player,
	.banker {
		font-size: 28px;
		text-align: center;
	}
	img {
		cursor: pointer;
		height: 120px;
	}
	.imgActive {
		border: 1px solid rgba(236, 192, 108, 1);
		box-shadow: 0 0 5px rgba(236, 192, 108, 1);
	}
	.rotate-right {
		transform: rotate(90deg);
		margin-right: 3.5%;
	}
	.rotate-left {
		transform: rotate(90deg);
		margin-left: 3.5%;
	}
}
.verify-text {
	font-size: 28px;
	color: #90ff3c;
}
.text-wrap {
	position: relative;

	#copy1,
	#copy2,
	#copy3 {
		position: absolute;
		right: 10px;
		bottom: 10px;
	}
}
.bottom {
	position: relative;
}
.equal {
	position: absolute;
	left: 48.5%;
	bottom: 20%;
	font-size: 66px;
	color: #90ff3c;
}
.down {
	position: absolute;
	width: 40px;
	height: 40px;
	left: 28%;
	top: 4%;
	z-index: 99;
}
textarea {
	display: block;
	resize: vertical;
	padding: 5px 15px;
	line-height: 1.5;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
	width: 100%;
	font-size: inherit;
	color: #606266;
	background-color: #fff;
	background-image: none;
	border: 1px solid #dcdfe6;
	border-radius: 4px;
	-webkit-transition: border-color 0.2s cubic-bezier(0.645, 0.045, 0.355, 1);
	transition: border-color 0.2s cubic-bezier(0.645, 0.045, 0.355, 1);
}
