			betStatusList: [
				this.$t('members.members_16'),
				this.$t('members.members_17'),
				this.$t('members.members_18'),
				this.$t('members.members_19'),
				this.$t('members.members_20'),
				this.$t('members.members_21')
			],
			form: {
				time: [+start, +end],
				time2: '',
				gameMode: -1,
				playerLoginName: '',
				id: '',
				roundNo: '',
				gameType: -1,
				betStatus: -1,
				anchorRoom: -1,
				currency: -1,
				betPoint: -1,
				recordType: -1,
				isEqLoginName: 0,
				betflag: -1,
				searchAgentId: '',
				playerId: '',
				loginIp: '',
				startBetAmount: '',
				endBetAmount: '',
				orderProp: 'betAt',
				order: 'descending',
				platformId: -1,
				deviceType: -1
			},
			amountData: {
				totalPlayerNum: '-',
				netAmountSum: '-',
				apiRate: '-',
				paymentAmountSum: '-',
				betAmountSum: '-',
				payAmountSum: '-',
				totalNum: '-',
				validBetAmountSum: '-',
				netRate: '-'
			},
			videoSrc: '',
			showVideo: false,
			currentData: {},
			currentData2: {},
			userAgentType: '',
			detailVisible: false,
			defaultColumn: store.get('betrecordColumn')
				? store.get('betrecordColumn')
				: [
						{
							id: 1,
							checked: true,
							cancel: true,
							show: false,
							name: 'members_betrecord_listcolumn_591'
						},
						{
