							checked: true,
							cancel: true,
							show: false,
							name: 'members_betrecord_listcolumn_594'
						},
						{
							id: 12,
							checked: true,
							cancel: true,
							show: false,
							name: 'members_betrecord_listcolumn_595'
						},
						{
							id: 13,
							checked: true,
							cancel: true,
							show: false,
							name: 'members_betrecord_listcolumn_596'
						},
						{
							id: 14,
							checked: true,
							cancel: true,
							show: false,
							name: 'members_betrecord_listcolumn_597'
						},
						{
							id: 17,
							checked: true,
							cancel: true,
							show: false,
							name: 'members_betrecord_listcolumn_598'
						},
						{
							id: 18,
							checked: true,
							cancel: true,
							show: false,
							name: 'members_betrecord_listcolumn_599'
						},
						{
							id: 19,
							checked: true,
							cancel: true,
							show: false,
							name: 'members_betrecord_listcolumn_600'
						},
						{
							id: 7,
							checked: true,
							cancel: true,
							show: false,
							name: 'members_betrecord_listcolumn_601'
						},
						{
							id: 10,
							checked: true,
							cancel: true,
							show: false,
							name: 'members_betrecord_listcolumn_602'
						},

						{
							id: 11,
							checked: true,
							cancel: true,
							show: false,
							name: 'members_betrecord_listcolumn_603'
						},
						{
							id: 22,
							checked: true,
							cancel: true,
							show: false,
							name: 'members_betrecord_listcolumn_604'
						},
						{
							id: 16,
							checked: true,
							cancel: true,
							show: false,
							name: 'members_betrecord_listcolumn_605'
						},
						// 默认不展示
						{
							id: 3,
							checked: false,
							cancel: true,
							show: true,
							name: 'members_betrecord_listcolumn_606'
						},
						{
							id: 5,
							checked: false,
							cancel: true,
							show: true,
							name: 'members_betrecord_listcolumn_607'
						},

						{
							id: 8,
							checked: false,
							cancel: true,
							show: true,
							name: 'members_betrecord_listcolumn_608'
						},
						{
							id: 9,
							checked: false,
							cancel: true,
							show: true,
							name: 'members_betrecord_listcolumn_609'
						},
						{
							id: 20,
							checked: false,
							cancel: true,
							show: true,
							name: 'members_betrecord_listcolumn_610'
						},
						{
							id: 21,
							checked: false,
							cancel: true,
							show: true,
							name: 'members_betrecord_listcolumn_611'
						},
						{
							id: 25,
							checked: false,
							cancel: true,
							show: true,
							name: 'new_68'
						},
						{
							id: 31,
							checked: false,
							cancel: true,
							show: true,
							name: 'members_list_components_historyrecord_638'
						},
						{
							id: 32,
							checked: false,
							cancel: true,
							show: true,
							name: 'merchant_dailyrateaccount_components_tablecomp_764'
						}
				  ],
			// 是否展示列表选择
			isShowChoiceColumn: false,
			prevIndex: 1,
			defaultSort: { prop: 'betAt', order: 'descending' },
			tableKey: +new Date(),
			roundNo: '',
			betPointList: {},
			platforms: []
		}
	},
	computed: {
		...mapGetters(['userInfo']),
		...mapGetters(['globalDics']),
		gametype() {
			const types = this.globalDics.gametype || []
			return types.map((item) => {
				return {
					value: item.value,
					label: item.text,
					zh: item.text,
					en: item.englishText
				}
			})
		},
		gamemode() {
			const types = this.globalDics.gamemode || []
			return types.map((item) => {
				return {
					value: item.value,
					label: item.text,
					zh: item.text,
					en: item.englishText
				}
			})
		},
		betstatus() {
			const types = this.globalDics.betstatus || []
			return types.map((item) => {
				return {
					value: item.value,
					label: item.text,
					zh: item.text,
					en: item.englishText
				}
			})
		},
		betflag() {
			const types = this.globalDics.betflag || []
			return types.map((item) => {
				return {
					value: item.value,
					label: item.text,
					zh: item.text,
					en: item.englishText
				}
			})
		},
		columns() {
			let arr = this.defaultColumn
			if (this.userInfo.agentCode !== 'AAAAA') {
				arr = arr.filter(({ id }) => id !== 32)
				arr = arr.filter(({ id }) => id !== 31)
			}
			return arr
		}
	},
	watch: {
		'form.time': function(val) {
			if (val) {
				this.form.time2 = ''
			}
		},
		'form.time2': function(val) {
			if (val) {
				this.form.time = ''
			}
		}
	},
	mounted() {
		this.loadCurrenyDate()
		this.loadbetPoint()
		document.addEventListener('click', this.showColumn)
		const w = this.$refs.content.getBoundingClientRect().width
		this.$refs.footerCon.style.width = w - 25 + 'px'
		window.onresize = () => {
			const w = this.$refs.content.getBoundingClientRect().width
			this.$refs.footerCon.style.width = w - 25 + 'px'
		}
	},
	destroyed() {
		document.removeEventListener('click', this.showColumn)
	},
	activated() {
		if (localStorage.getItem('betPoint')) {
			this.form.isEqLoginName = 1
			this.reloadData()
		}
		if (localStorage.getItem('roundNo2')) {
			this.form.roundNo = localStorage.getItem('roundNo2')
			this.form.playerId = localStorage.getItem('playerId2')
			localStorage.removeItem('roundNo2')
			localStorage.removeItem('playerId2')
			this.form.isEqLoginName = 1
			this.form.time = []
			this.loadData()
		}
	},
	methods: {
		classFilter(item) {
			switch (item) {
				case 0:
					return 'successState'
				case 1:
					return 'disableState'
				case 2:
					return 'dangerState'
				case 4:
					return 'warningState'
				case 6:
					return 'infoState'

				default:
					break
			}
		},
		// 失去焦点的时候重置
		onTimeBlur() {
			this.minDate = null
		},
		getAmoutColor(value) {
			if (value > 0) {
				return 'green'
			} else if (value < 0) {
				return 'red'
			}
			return ''
		},

		loadCurrenyDate() {
			this.$api
				.getCurrencyComboBoxList({
					pageIndex: 1,
					pageSize: 1000
				})
				.then((_) => {
					const data = _.data
					this.currencyList = data.record
				})
		},
		loadbetPoint() {
			this.$api.betPoint().then((_) => {
				const data = _.data
				this.betPointList = data
			})
		},
		deviceTypeFilter(value) {
			console.log(value)
			let label = ''
			this.deviceTypeList.forEach((item) => {
				if (Number(item.value) === Number(value)) {
					label = item.label
				}
			})
			return label
		},
		showColumn(e) {
			const icon = document.getElementById('icon')
			const list = document.getElementById('list')
			if (icon && !icon.contains(e.target) && list && !list.contains(e.target)) {
				this.isShowChoiceColumn = false
			}
		},
		gameTypeChange() {
			this.form.betPoint = -1
		},
		reloadData() {
			this.loading = true
			const initData = {
				pageSize: 100000,
				pageIndex: this.pageIndex
			}
			const requestData = {
				roundNo: localStorage.getItem('roundNo'),
				playerId: localStorage.getItem('playerId'),
				betPointName: localStorage.getItem('betPoint')
			}
			localStorage.removeItem('roundNo')
			localStorage.removeItem('playerId')
			localStorage.removeItem('betPoint')
			const form = {
				...initData,
				...requestData
			}
			this.$api
				.memberQueryBetRecord(form)
				.then((_) => {
					this.loading = false
					if (_.code === 200) {
						const data = _.data
						this.list = data.record
						this.amountData = data.summary || {
							totalPlayerNum: '-',
							netAmountSum: '-',
							apiRate: '-',
							paymentAmountSum: '-',
							betAmountSum: '-',
							payAmountSum: '-',
							totalNum: '-',
							validBetAmountSum: '-',
							netRate: '-'
						}
						this.total = data.totalRecord
					}
				})
				.catch(() => {
					this.pageIndex = this.prevIndex
					this.list = []
					this.amountData = {
						totalPlayerNum: '-',
						netAmountSum: '-',
						apiRate: '-',
						paymentAmountSum: '-',
						betAmountSum: '-',
						payAmountSum: '-',
						totalNum: '-',
						validBetAmountSum: '-',
						netRate: '-'
					}
					this.total = 0
					this.loading = false
				})

			this.$api.queryGamingRoom().then((res) => {
				this.platforms = res.data
			})
		},
		recordTypeFilter(value) {
			let label = ''
			this.recordList.forEach((item) => {
				if (Number(item.value) === Number(value)) {
					label = item.label
				}
			})
			return label
		},
		loadData() {
			if (localStorage.getItem('playerId')) {
				this.form.playerLoginName = localStorage.getItem('playerLoginName')
				this.form.playerId = localStorage.getItem('playerId')
				this.form.isEqLoginName = 1
				localStorage.removeItem('playerId')
				localStorage.removeItem('playerLoginName')
			}
			if (!this.form.time[0] && !this.form.roundNo && !this.form.time2[0]) {
				this.$message({
					type: 'error',
					message: this.$t('wallet_11')
				})
				return
			}
			if (this.form.time[0] < 1556640000000) {
				this.$message({
					message: this.$t('timeerror'),
					type: 'error'
				})
				return
			}
			this.loading = true
			const initData = {
				pageSize: this.pageSize,
				pageIndex: this.pageIndex,
				startBetTime: this.form.time[0],
				endBetTime: this.form.time[1],
				startUpdateTime: this.form.time2[0],
				endUpdateTime: this.form.time2[1],
				playerLoginName: this.form.playerLoginName,
				anchorRoom: this.form.anchorRoom,
				searchAgentId: this.form.agentId,
				recordType: this.form.recordType,
				currency: this.form.currency,
				isEqLoginName: this.form.isEqLoginName,
				betPoint: this.form.betPoint,
				loginIp: this.form.loginIp,
				startBetAmount: this.form.startBetAmount,
				endBetAmount: this.form.endBetAmount,
				orderProp: this.form.orderProp,
				order: this.form.order,
				roundNo: this.form.roundNo,
				playerId: this.form.playerId,
				platformId: this.form.platformId,
				deviceType: this.form.deviceType
			}
			const requestData = this.hadSearch
				? {
						gameMode: this.form.gameMode,
						id: this.form.id,
						gameType: this.form.gameType,
						betStatus: this.form.betStatus,
						betFlag: this.form.betflag
				  }
				: {}
			const form = {
				...initData,
				...requestData
			}
			this.$api
				.memberQueryBetRecord(form)
				.then((_) => {
					this.loading = false
					if (_.code === 200) {
						const data = _.data
						this.list = data.record
						this.amountData = data.summary || {
							totalPlayerNum: '-',
							netAmountSum: '-',
							apiRate: '-',
							paymentAmountSum: '-',
							betAmountSum: '-',
							payAmountSum: '-',
							totalNum: '-',
							validBetAmountSum: '-',
							netRate: '-'
						}
						this.total = data.totalRecord
					}
				})
				.catch(() => {
					this.pageIndex = this.prevIndex
					this.list = []
					this.amountData = {
						totalPlayerNum: '-',
						netAmountSum: '-',
						apiRate: '-',
						paymentAmountSum: '-',
						betAmountSum: '-',
						payAmountSum: '-',
						totalNum: '-',
						validBetAmountSum: '-',
						netRate: '-'
					}
					this.total = 0
					this.loading = false
				})

			this.$api.queryGamingRoom().then((res) => {
				console.log('res :>> ', res)
				this.platforms = res.data
			})
		},
		filterGameMode(c) {
			const arr = this.gamemode || []
			const find = arr.find((item) => Number(item.value) === Number(c))
			if (Cookies.get('language') === 'zh') {
				return (find && find.zh) || '-'
			}
			if (Cookies.get('language') === 'en') {
				return (find && find.en) || '-'
			}
			return '-'
		},
		filterBetflag(c) {
			const arr = this.betflag || []
			const find = arr.find((item) => Number(item.value) === Number(c))
			if (Cookies.get('language') === 'zh') {
				return (find && find.zh) || '-'
			}
			if (Cookies.get('language') === 'en') {
				return (find && find.en) || '-'
			}
			return '-'
		},
		handleCurrentChange(val) {
			this.prevIndex = this.pageIndex
			this.pageIndex = val
			this.loadData()
		},
		handleReset() {
			if (this.$route.query && JSON.stringify(this.$route.query) !== '{}') {
				router.push(this.$route.path, () => {
					this.resetCommon()
				})
			} else {
				this.resetCommon()
			}
		},
		resetCommon() {
			const end = dayjs().endOf('day')
			const start = dayjs().startOf('day')

			this.hadSearch = false
			this.form = {
				time: '',
				time2: [+start, +end],
				gameMode: -1,
				playerLoginName: '',
				id: '',
				roundNo: '',
				playerId: '',
				gameType: -1,
				anchorRoom: -1,
				betStatus: -1,
				currency: -1,
				isEqLoginName: 0,
				betPoint: -1,
				recordType: -1,
				platformId: -1,
				deviceType: -1,
				betflag: -1,
				searchAgentId: '',
				orderProp: 'betAt',
				order: 'descending'
			}
			this.defaultSort = { prop: 'betAt', order: 'descending' }
			this.$refs.ParentTable.clearSort()
			this.tableKey = +new Date()
			this.initList()
		},
		handleSearch() {
			this.pageIndex = 1
			this.hadSearch = true
			this.loadData()
		},
		getSummaries(param) {
			const { columns } = param
			const sums = []
			columns.forEach((column, index) => {
				if (!column.property && index === 0) {
					sums[index] = this.$t('members_betrecord_index_580')
					return
				}
				if (column.property === 'roundNo') {
					sums[index] =
						this.$t('members_betrecord_index_554') +
						this.formatCurrency(this.amountData[`betAmountSum`])
				}
				if (column.property === 'agentName') {
					sums[index] =
						this.$t('members_betrecord_index_555') +
						this.formatCurrency(this.amountData[`validBetAmountSum`])
				}

				if (column.property === 'playerLoginName') {
					sums[index] =
						this.$t('members_betrecord_index_556') +
						this.formatCurrency(this.amountData[`payAmountSum`])
				}
				if (column.property === 'betAt') {
					sums[index] =
						this.$t('members_betrecord_index_557') +
						this.formatCurrency(this.amountData[`netAmountSum`])
				}
				if (column.property === 'betPoint') {
					sums[index] =
						this.$t('members_betrecord_index_558') + this.amountData[`apiRate`]
				}
				if (column.property === 'betAmount') {
					sums[index] =
						this.$t('members_betrecord_index_559') +
						this.amountData[`paymentAmountSum`]
				}
				if (column.property === 'validBetAmount') {
					sums[index] =
						this.$t('members_betrecord_index_560') + this.amountData[`netRate`]
				}
			})

			return sums
		},
		checkDetail(row) {
			this.detailVisible = true
			this.currentData = { ...row }
		},
		showChoiceColumn() {
			this.isShowChoiceColumn = !this.isShowChoiceColumn
		},
		showResult(row) {
			this.dialogShow = true
			this.currentData = { ...row }
		},
		showCardDetail(row) {
			this.dialogShow2 = true
			this.roundNo = row.roundNo
		},
		handleShowVideo(path, row) {
			this.showVideo = true
			this.videoSrc = path
			this.currentData = { ...row }
		},
		checked(id) {
			const index = this.columns.findIndex((val) => val.id === id)
			if (index > -1) {
				return this.columns[index].checked
			}
		},
		onCheckBoxChange(value, id) {
			const index = this.defaultColumn.findIndex((val) => val.id === id)
			if (index > -1) {
				this.defaultColumn.splice(index, 1, {
					...this.columns[index],
					checked: value
				})

				store.set('betrecordColumn', this.defaultColumn)
			}
		},
		// ip登录轨迹
		checkLoginIp(row) {
			this.drawer = true
			this.ipData = { ...row }
		},
		sortChange(val) {
			if (localStorage.getItem('betPoint')) {
				return localStorage.removeItem('betPoint')
			}
			if (localStorage.getItem('playerId')) {
				return
			}
			if (this.loading) {
				return
			}
			const { prop, order } = val
			if (order === null) {
				this.form.orderProp === 'descending'
					? (val.order = 'descending')
					: (val.order = 'ascending')
				val.column.order = val.order
				this.form.orderProp = prop
				this.form.order = val.order
				this.loadData()
				return val
			}
			this.form.orderProp = prop
			this.form.order = order
			this.defaultSort = { prop, order: order || 'descending' }
			this.loadData()
			return val
		},
		clearSrc() {
			this.videoSrc = ''
		},
		showHistory(row) {
			if (this.hasPermission(119000015)) {
				if (row.betFlag === 2 || row.betFlag === 4) {
					return true
				} else if (row.gameTypeId === 2012 && row.betFlag === 1) {
					return true
				} else {
					return false
				}
			} else {
				return false
			}
		},
		filterGametype(c) {
			const arr = this.gametype || []
			const find = arr.find((item) => item.label === c)
			if (Cookies.get('language') === 'zh') {
				return (find && find.zh) || '-'
			}
			if (Cookies.get('language') === 'en') {
				return (find && find.en) || '-'
			}
			return '-'
		},
		filterBetstatus(c) {
			const arr = this.betstatus || []
			const find = arr.find((item) => item.value === c)
			if (Cookies.get('language') === 'zh') {
				return (find && find.zh) || '-'
			}
			if (Cookies.get('language') === 'en') {
				return (find && find.en) || '-'
			}
			return '-'
		},
		getTrueName(name) {
			return i18nName(name)
		}
	}
