import list from '@/mixins/list'
import Modal from '@/components/Modal'
export default {
	components: { Modal },
	mixins: [list],
	data() {
		return {
			dialogVisible: false,
			typeD: true,
			form: {
				playerName: '',
				patchType: 1,
				eventReason: 1,
				billNo: '',
				remark: '',
				amount: ''
			}
		}
	},
	computed: {
		notice() {
			let word = ''
			const word2 =
				this.form.patchType === 1 ? this.$t('agent_19') : this.$t('agent_20')
			word =
				this.$t('agent_18') +
				this.form.playerName +
				word2 +
				this.form.amount +
				this.$t('agent_21')
			return word
		},
		rules() {
			return {
				playerName: [
					{
						required: true,
						trigger: 'blur',
						message: this.$t('backstage_401019')
					}
				],
				amount: [
					{ required: true, trigger: 'blur', message: this.$t('backstage_401020') }
				],
				remark: [
					{ required: true, trigger: 'blur', message: this.$t('backstage_401025') }
				]
			}
		}
	},
	mounted() {
		this.newNumber()
	},
	destroyed() {},
	methods: {
		changeReason() {
			if (this.form.eventReason === 1) {
				this.form.patchType = 1
				this.typeD = true
			} else {
				this.typeD = false
			}
		},
		newNumber() {
			this.$api
				.acctPatchBillNoGene()
				.then((_) => {
					if (_.code === 200) {
						this.form.billNo = _.data
					}
				})
				.catch(() => {})
		},
		handleSure() {
			const loading = this.$loading({
				lock: true,
				text: 'Loading',
				spinner: 'el-icon-loading',
				background: 'rgba(0, 0, 0, 0.7)'
			})
			this.form.billNo = this.form.billNo + ''
			this.$api
				.acctPatch(this.form)
				.then((_) => {
					if (_.code === 200) {
						this.dialogVisible = false
						if (_.data === 'SUCCESS') {
							this.$message({
								message: this.$t('merchant_merchantinfo_components_keysmanage_1004'),
								type: 'success'
							})
						} else {
							this.$message({
								message: _.data,
								type: 'success'
							})
						}
						this.form = {
							playerName: '',
							patchType: 1,
							eventReason: 1,
							remark: '',
							amount: '',
							billNo: ''
						}
						this.typeD = true
						this.newNumber()
					}
					loading.close()
				})
				.catch(() => {
					loading.close()
					this.dialogVisible = false
				})
		},
		openModal() {
			this.$refs.form.validate((valid) => {
				if (valid) {
					this.dialogVisible = true
				}
			})
		}
	}
}
