import list from '@/mixins/list'
import { mapGetters } from 'vuex'
import { routerNames } from '@/utils/consts'
import { generateTitle } from '@/utils/i18n'
import { decimalValidateSix } from '@/utils/validate'

export default {
	name: routerNames.systemCurrency,
	mixins: [list],
	data() {
		return {
			hadSearch: false,
			loading: false,
			releaseTime: '',
			form: {
				id: -1
			},
			urlDialogVisible: false,
			urlDialogForm: {
				agentName: '',
				type: '',
				url: '',
				desc: ''
			},
			userNames: [],
			currencyForm: {
				code: '',
				name: '',
				currencyRate: ''
			},
			ipDialogVisible: false,
			isEdit: true,
			currentRow: {}
		}
	},
	computed: {
		...mapGetters(['globalDics']),
		ipDialogRules() {
			/* eslint-disable */
			const validateName = async (rule, value, callback) => {
				if (!/^[a-zA-Z0-9\u4e00-\u9fa5]+$/.test(value)) {
					callback(new Error(this.$t('systemmanagement_currency_index_16')))
				} else if (value.length > 20) {
					callback(new Error(this.$t('systemmanagement_parameter_index_25')))
				} else if (value.length < 2) {
					callback(new Error(this.$t('systemmanagement_parameter_index_26')))
				} else {
					callback()
				}
			}

			/* eslint-disable */
			const validateCode = async (rule, value, callback) => {
				if (!/^[A-Z]{3}$/.test(value)) {
					callback(new Error(this.$t('systemmanagement_currency_index_78')))
				} else {
					callback()
				}
			}

			const currencyRateValidate = (rule, value, callback) => {
				if (!decimalValidateSix(value)) {
					callback(new Error(this.$t('systemmanagement_currency_index_77')))
				} else if (value === 0 || value === '0') {
					callback(new Error(this.$t('systemmanagement_currency_index_81')))
				} else if (value < 0 || value > 10000) {
					callback(new Error(this.$t('systemmanagement_currency_index_82')))
				} else {
					callback()
				}
			}
			return {
				name: [
					{
						required: true,
						message: this.$t('systemmanagement_currency_index_1343'),
						trigger: 'blur'
					},
					{
						validator: validateName,
						trigger: 'blur'
					}
				],
				code: [
					{
						required: true,
						message: this.$t('systemmanagement_currency_index_1344'),
						trigger: 'blur'
					},
					{
						validator: validateCode,
						trigger: 'blur'
					}
				],
				currencyRate: [
					{
						required: true,
						validator: currencyRateValidate,
						trigger: 'blur'
					}
				]
			}
		}
	},
	watch: {
		// 'currencyForm.currencyRate'(val) {
		// 	if (val === 0 || val === '0') {
		// 		this.currencyForm.currencyRate = ''
		// 	}
		// },
	},
	created() {
		// 获取操作账号
		this.$api.listUsername().then((res) => {
			if (res.code === 200) {
				this.userNames = res.data
			}
		})
	},
	mounted() {
		this.itemtimer = setInterval(() => {
			const items = document.querySelectorAll('.el-form-item__error')
			items.forEach((i) => {
				if (i.innerHTML.includes(this.$t('systemmanagement_currency_index_16'))) {
					i.innerHTML = this.$t('systemmanagement_currency_index_16')
				}
				if (i.innerHTML.includes(this.$t('systemmanagement_currency_index_17'))) {
					i.innerHTML = this.$t('systemmanagement_currency_index_17')
				}
				if (i.innerHTML.includes('输入3个大写英文字符')) {
					if (/^[A-Z]{3}$/.test(this.currencyForm.code)) {
						i.style.opacity = '0'
						return
					}
					let elem = document.createElement('div')
					elem.className = 'el-form-item__error'
					elem.innerHTML = '输入3个大写英文字符'
					i.parentElement.replaceChild(elem, i)
				}
				if (i.innerHTML.includes('Enter 3 uppercase English characters')) {
					if (/^[A-Z]{3}$/.test(this.currencyForm.code)) {
						i.style.opacity = '0'
						return
					}
					let elem = document.createElement('div')
					elem.className = 'el-form-item__error'
					elem.innerHTML = 'Enter 3 uppercase English characters'
					i.parentElement.replaceChild(elem, i)
				}
				if (i.innerHTML.includes(this.$t('systemmanagement_currency_index_1344'))) {
					i.innerText = ' ' + this.$t('systemmanagement_currency_index_1344')
				}
			})
		}, 1000)
	},
	destroyed() {
		this.itemtimer = null
		window.clearInterval(this.itemtimer)
	},
	methods: {
		renderInfo(modifys, item) {
			let newValue
			if (item.includes(this.$t('systemmanagement_currency_index_1349'))) {
				newValue = []
				modifys && modifys.map((val) => newValue.push(generateTitle(val)))
			} else {
				newValue = modifys
			}
			return newValue
		},
		hadChangeColor(pre, next) {
			if (JSON.stringify(pre) !== JSON.stringify(next)) {
				return 'change-color'
			}
		},
		loadData() {
			this.loading = true
			const initData = {
				pageIndex: this.pageIndex,
				pageSize: this.pageSize
			}
			const requestData = this.hadSearch
				? {
						id: this.form.id
				  }
				: {}
			const form = {
				...initData,
				...requestData
			}
			this.$api
				.getCurrencyList(form)
				.then((_) => {
					this.loading = false
					const data = _.data
					this.list = data.record
					this.total = data.totalRecord
				})
				.catch(() => {
					this.loading = false
				})
		},
		handleCurrentChange(val) {
			this.loadData()
		},
		handleReset() {
			this.form = {
				id: -1
			}
			this.hadSearch = false
			this.initList()
		},
		handleSearch() {
			this.hadSearch = true
			this.pageIndex = 1
			this.loadData()
		},
		editRow(row) {
			this.currentRow = row
			this.isEdit = true
			this.ipDialogVisible = true
			this.currencyForm = {
				code: row.code,
				name: row.name,
				currencyRate: row.currencyRate
			}
		},
		stopRow(index, rows) {
			rows.splice(index, 1)
		},
		handleCreateWhite() {
			this.isEdit = false
			this.ipDialogVisible = true
			this.currencyForm = {
				code: '',
				name: '',
				currencyRate: ''
			}
		},
		onCancel() {
			this.ipDialogVisible = false
			this.$refs.currencyFormRef.clearValidate()
		},
		commitIpDialog() {
			this.$refs.currencyFormRef.validate((val) => {
				if (val) {
					const form = this.isEdit
						? {
								name: this.currencyForm.name,
								code: this.currencyForm.code,
								currencyRate: this.currencyForm.currencyRate,
								id: this.currentRow.id
						  }
						: {
								name: this.currencyForm.name,
								code: this.currencyForm.code,
								currencyRate: this.currencyForm.currencyRate
						  }
					const api = this.isEdit ? 'editCurrency' : 'addCurrency'
					const text = this.isEdit
						? this.$t('systemmanagement_currency_index_1341')
						: this.$t('systemmanagement_currency_index_1350')
						const loading = this.$loading({
				lock: true,
				text: 'Loading',
				spinner: 'el-icon-loading',
				background: 'rgba(0, 0, 0, 0.7)'
			})
					this.$api[api](form).then(() => {
						this.ipDialogVisible = false
						this.$message({
							type: 'success',
							message: this.$t('systemmanagement_currency_index_1351', [text])
						})
						this.loadData()
						loading.close()
					}).catch(() => {
						loading.close()
					})
				}
			})
		}
	}
}
