.root {
	position: relative;
	display: inline-block;
	.topAgent {
		top: 40px !important;
		left: 82px !important;
	}
	.down-wrapper {
		max-height: 466px;
		overflow: auto;
		padding: 20px;
		min-width: 318px;
		position: absolute;
		top: 30px;
		left: 100px;
		border: 1px solid #ddd;
		background: #fff;
		z-index: 999;
	}
}
.mytree /deep/ {
	.el-tree > .el-tree-node:after {
		border-top: none;
	}
	.el-tree-node {
		position: relative;
		padding-left: 16px;
	}
	//节点有间隙，隐藏掉展开按钮就好了,如果觉得空隙没事可以删掉
	.el-tree-node__expand-icon.is-leaf {
		display: none;
	}
	.el-tree-node__children {
		padding-left: 16px;
	}

	.el-tree-node :last-child:before {
		height: 38px;
	}

	.el-tree > .el-tree-node:before {
		border-left: none;
	}

	.el-tree > .el-tree-node:after {
		border-top: none;
	}

	.el-tree-node:before {
		content: '';
		left: -4px;
		position: absolute;
		right: auto;
		border-width: 1px;
	}

	.el-tree-node:after {
		content: '';
		left: -4px;
		position: absolute;
		right: auto;
		border-width: 1px;
	}

	.el-tree-node:before {
		border-left: 1px solid #c0c4cc;
		bottom: 0px;
		height: 100%;
		top: -26px;
		width: 1px;
	}

	.el-tree-node:after {
		border-top: 1px solid #c0c4cc;
		height: 20px;
		top: 12px;
		width: 24px;
	}
}
