import HistoryRecord from '@/views/abnormalRound/players/components/HistoryRecord'
import BetDetail from '@/views/abnormalRound/players/components/BetDetail'
import list from '@/mixins/list'
import { mapGetters } from 'vuex'
import dayjs from 'dayjs'
import Video from '@/components/Video'
import ResultShow from '@/components/ResultShow'
import Cookies from 'js-cookie'

export default {
	name: 'IPLogin',
	components: { HistoryRecord, Video, ResultShow, BetDetail },
	mixins: [list],
	props: {
		drawer: {
			type: Boolean,
			default: () => false
		},
		ipdata: {
			type: Object,
			default: () => {}
		},
		page: {
			type: String,
			default: 'default'
		}
	},
	doNotInit: true,
	data() {
		return {
			dialogShow: false,
			loading: false,
			elDrawer: true,
			recordData: null,
			historyRecordShow: false,
			videoSrc: '',
			showVideo: false,
			currentData: {},
			detailVisible: false
		}
	},
	computed: {
		...mapGetters(['globalDics']),
		agenttype() {
			return this.globalDics.agenttype
		},
		gametype() {
			const types = this.globalDics.gametype || []
			return types.map((item) => {
				return {
					value: item.value,
					label: item.text,
					zh: item.text,
					en: item.englishText
				}
			})
		},
		gamemode() {
			const types = this.globalDics.gamemode || []
			return types.map((item) => {
				return {
					value: item.value,
					label: item.text,
					zh: item.text,
					en: item.englishText
				}
			})
		},
		gameresultstatus() {
			return this.globalDics.gameresultstatus
		},
		betstatus() {
			return this.globalDics.betstatus
		},
		betflag() {
			const types = this.globalDics.betflag || []
			return types.map((item) => {
				return {
					value: item.value,
					label: item.text,
					zh: item.text,
					en: item.englishText
				}
			})
		}
	},
	watch: {
		drawer: {
			handler(val) {
				this.elDrawer = val
			},
			immediate: true
		},
		ipdata: {
			handler(val) {
				if (val) {
					this.loadData()
				}
			},
			immediate: true
		}
	},
	methods: {
		formatTime(time) {
			return dayjs(time).format('YYYY-MM-DD hh:mm:ss')
		},
		loadData() {
			this.loading = true

			let abnormalType = ''
			if (this.ipdata.abnormalType) {
				abnormalType = this.ipdata.abnormalType
			} else {
				if (this.ipdata.gameTypeId === 2012 && this.ipdata.betFlag === 1) {
					abnormalType = 3
				} else {
					abnormalType =
						this.ipdata.betFlag === 2 ? 1 : this.ipdata.betFlag === 4 ? 2 : ''
				}
			}

			const form =
				this.page === 'default'
					? {
							pageSize: this.pageSize,
							pageIndex: this.pageIndex,
							playerId: this.ipdata.playerId,
							abnormalType,
							roundNo: this.ipdata.roundNo ? this.ipdata.roundNo : this.ipdata.roundId
					  }
					: {
							pageSize: this.pageSize,
							pageIndex: this.pageIndex,
							playerId: this.ipdata.playerId,
							abnormalType,
							roundNo: this.ipdata.roundNo ? this.ipdata.roundNo : this.ipdata.roundId,
							betId: this.ipdata.id
					  }

			const api =
				this.page === 'default'
					? 'originalBettingRecordList'
					: 'originalBettingRecord'

			this.$api[api](form)
				.then((_) => {
					if (_.code === 200) {
						if (this.page === 'default') {
							const data = _.data
							this.list = data
							this.total = data.length
						} else {
							const data = _.data
							this.list = [data]
							this.total = 1
						}
					}
					this.loading = false
				})
				.catch((err) => {
					if (err) {
						this.loading = false
					}
				})
		},
		handleIP(data) {
			this.historyRecordShow = true
			this.recordData = data
		},
		handleSearch() {
			if (!this.form.time || this.form.time === '') {
				return this.$message({
					message: this.$t('abnormalround_players_components_iplogin_121'),
					type: 'error',
					duration: 5 * 1000
				})
			}
			this.minDate = null
			this.loadData()
		},
		handleReset() {
			const end = dayjs()
				.endOf('day')
				.valueOf()
			const start = dayjs()
				.startOf('day')
				.subtract(6, 'd')
				.valueOf()
			this.minDate = null
			this.form = {
				time: [start, end]
			}
			this.initList()
		},
		handleCurrentChange() {
			this.loadData()
		},
		handleClose() {
			this.$emit('update:drawer', false)
		},
		getAmoutColor(value) {
			if (value > 0) {
				return 'green'
			} else if (value < 0) {
				return 'red'
			}
			return ''
		},
		filterGameMode(c) {
			const arr = this.gamemode || []
			const find = arr.find((item) => Number(item.value) === Number(c))
			if (Cookies.get('language') === 'zh') {
				return (find && find.zh) || '-'
			}
			if (Cookies.get('language') === 'en') {
				return (find && find.en) || '-'
			}
			return '-'
		},
		filterBetflag(c) {
			const arr = this.betflag || []
			const find = arr.find((item) => Number(item.value) === Number(c))
			if (Cookies.get('language') === 'zh') {
				return (find && find.zh) || '-'
			}
			if (Cookies.get('language') === 'en') {
				return (find && find.en) || '-'
			}
			return '-'
		},
		filterBetStatus(value) {
			return value === 0
				? this.$t('abnormalround_players_components_iplogin_122')
				: this.$t('abnormalround_players_components_iplogin_123')
		},
		handleShowVideo(path, row) {
			this.showVideo = true
			this.videoSrc = path
			this.currentData = { ...row }
		},
		checkDetail(row) {
			this.detailVisible = true
			this.currentData = { ...row }
		},
		showResult(row) {
			this.dialogShow = true
			this.currentData = { ...row }
		},
		clearSrc() {
			this.videoSrc = ''
		},
		filterGametype(c) {
			const arr = this.gametype || []
			const find = arr.find((item) => item.label === c)
			if (Cookies.get('language') === 'zh') {
				return (find && find.zh) || '-'
			}
			if (Cookies.get('language') === 'en') {
				return (find && find.en) || '-'
			}
			return '-'
		}
	}
}
