import list from '@/mixins/list'
import tier from '@/mixins/tier'
import TableComp from './components/TableComp'
import TableModal from './components/TableModal'
import { mapGetters } from 'vuex'
import dayjs from 'dayjs'
import { routerNames } from '@/utils/consts'
import SelectAgentTree from '@/components/SelectAgentTree'
import downloadSure from '@/utils/common/downloadSure'
import router from '@/router'

export default {
	name: routerNames.dotdeal,
	components: { SelectAgentTree, TableComp, TableModal },
	mixins: [list, tier],
	data() {
		const end = dayjs()
			.endOf('day')
			.subtract(1, 'd')
			.valueOf()
		const start = dayjs()
			.subtract(1, 'd')
			.startOf('day')
			.valueOf()
		return {
			// 交收明细弹窗
			drawer: false,
			detailData: null,
			hadSearch: false,
			loading: false,
			form: {
				time: [start, end],
				agentId: ''
			},
			currencyList: []
		}
	},
	computed: {
		...mapGetters(['globalDics']),
		trantype() {
			return this.globalDics.trantype
		}
	},
	methods: {
		loadData() {
			this.loading = true

			// this.loadCurrenyDate()

			const pageData = {
				pageSize: this.pageSize,
				pageIndex: this.pageIndex,
				startTime: this.form.time[0],
				endTime: this.form.time[1],
				type: 1
			}
			const requestData = this.hadSearch
				? {
						searchAgentId: this.form.agentId
				  }
				: {}
			const form = {
				...pageData,
				...requestData
			}

			this.$api
				.pointIncomeReport(form)
				.then((_) => {
					this.loading = false
					if (_.code === 200) {
						const data = _.data
						this.total = data.totalRecord
						const list = data.record
						this.tableLists = [
							{
								opened: false,
								iconLoading: false,
								level: 1,
								data: list
							}
						]
					}
				})
				.catch(() => {
					this.loading = false
				})
		},
		/**
		 * actType 1为增加 2为修改
		 */
		modifyData(level, params, tableIndex, actType) {
			const extraParams = {
				startTime: this.form.time[0],
				endTime: this.form.time[1],
				searchAgentId: params.agentId
			}
			this.mixinModifyData(
				level,
				params,
				tableIndex,
				actType,
				'pointIncomeReport',
				extraParams
			)
		},
		handleSearch() {
			const extraParams = {
				startTime: this.form.time[0],
				endTime: this.form.time[1]
			}
			this.mixinHandleSearch('pointIncomeReport', extraParams)
		},
		handleReset() {
			const end = dayjs()
				.endOf('day')
				.subtract(1, 'd')
				.valueOf()
			const start = dayjs()
				.subtract(1, 'd')
				.startOf('day')
				.valueOf()
			this.hadSearch = false
			this.form = {
				time: [start, end],
				agentId: ''
			}
			this.levels = []
			this.initList()
		},
		checkRecord(row) {
			this.$store.dispatch('tagsView/delView', {
				name: routerNames.dotaccount
			})
			this.$nextTick(() => {
				const targetUrl = '/merchant/dotaccount'

				router.push(`${targetUrl}?agentId=${row.agentId}&accountType=3`)
			})
		},
		checkDetail(row, params = {}) {
			const newParams = {
				ifToday: 2,
				pageSize: this.pageSize,
				pageIndex: this.pageIndex,
				startTime: this.form.time[0],
				endTime: this.form.time[1],
				searchAgentId: row.agentId,
				...params
			}
			this.$api.pointPayGetRecord(newParams).then((res) => {
				if (res.code === 200) {
					this.drawer = true
					const data = res.data
					const total = data.totalRecord
					const list = data.record
					this.detailData = {
						total,
						list,
						currentAgentId: row.agentId || params.searchAgentId
					}
				}
			})
		},
		exportCsv() {
			const params = {
				startTime: this.form.time[0],
				endTime: this.form.time[1],
				searchAgentId: this.form.agentId
			}
			downloadSure(
				this.$api,
				'pointPayGetRecordCSV',
				params,
				this.$t('merchant_dotdeal_index_918')
			)
		}
	}
}
