import list from '@/mixins/list'
import tier from '@/mixins/tier'
import { mapGetters } from 'vuex'
import dayjs from 'dayjs'
import { routerNames } from '@/utils/consts'
import SelectAgentTree from '@/components/SelectAgentTree'
import TableComp from './components/TableComp'

export default {
	name: routerNames.settlement,
	components: { SelectAgentTree, TableComp },
	mixins: [list, tier],
	data() {
		const end = dayjs()
			.endOf('day')
			.subtract(1, 'd')
			.valueOf()
		const start = dayjs()
			.startOf('day')
			.subtract(1, 'd')
			.valueOf()
		return {
			timestamp: Date.now(),
			hadSearch: false,
			loading: false,
			canDownload: true,
			canNoticeTotal: true,
			canNoticeTime: true,
			form: {
				time: [start, end],
				agentId: ''
			},
			tableLists: [{}],
			currencyList: []
		}
	},
	computed: {
		...mapGetters(['globalDics', 'userInfo']),
		trantype() {
			return this.globalDics.trantype
		}
	},
	methods: {
		loadData() {
			this.loading = true
			const pageData = {
				pageSize: this.pageSize,
				pageIndex: this.pageIndex,
				startTime: this.form.time[0],
				endTime: this.form.time[1],
				type: 1
			}
			const requestData = this.hadSearch
				? {
						agentId: this.form.agentId
				  }
				: {}
			const form = {
				...pageData,
				...requestData
			}
			this.$api
				.settlement(form)
				.then((_) => {
					this.loading = false
					if (_.code === 200) {
						const { data } = _
						const lists = data.record
						this.total = data.totalRecord
						this.tableLists = [
							{
								opened: false,
								iconLoading: false,
								level: 1,
								data: lists
							}
						]
					}
				})
				.catch(() => {
					this.loading = false
				})
		},
		/**
		 * actType 1为增加 2为修改
		 */
		modifyData(level, params, tableIndex, actType) {
			const extraParams = {
				startTime: this.form.time[0],
				endTime: this.form.time[1],
				agentId: params.agentId,
				order: params.order,
				orderProp: params.orderProp
			}
			this.mixinModifyData(
				level,
				params,
				tableIndex,
				actType,
				'settlement',
				extraParams
			)
		},
		handleSearch() {
			const extraParams = {
				startTime: this.form.time[0],
				endTime: this.form.time[1]
			}
			this.mixinHandleSearch('settlement', extraParams)
		},
		handleReset() {
			const end = dayjs()
				.endOf('day')
				.subtract(1, 'd')
				.valueOf()
			const start = dayjs()
				.startOf('day')
				.subtract(1, 'd')
				.valueOf()
			this.hadSearch = false
			this.form = {
				time: [start, end],
				agentId: ''
			}
			this.timestamp = Date.now()
			this.initList()
		},
		getAmoutColor(value) {
			if (value > 0) {
				return 'green'
			} else if (value < 0) {
				return 'red'
			}
			return ''
		},
		exportCsv() {
			if (Number(this.total) > 20000) {
				if (this.canNoticeTotal) {
					this.$message({
						message: this.$t('new_2'),
						type: 'error'
					})
					this.canNoticeTotal = false
					setTimeout(() => {
						this.canNoticeTotal = true
					}, 3000)
				}
				return
			}
			if (!this.canDownload) {
				if (this.canNoticeTime) {
					this.$message({
						message: this.$t('new_1'),
						type: 'error'
					})
					this.canNoticeTime = false
					setTimeout(() => {
						this.canNoticeTime = true
					}, 3000)
				}
				return
			}
			this.canDownload = false
			setTimeout(() => {
				this.canDownload = true
			}, 60000)
			const params = {
				startTime: this.form.time ? this.form.time[0] : '',
				endTime: this.form.time ? this.form.time[1] : ''
			}
			const startday = dayjs(params.startTime).get('date')
			const startmonth = dayjs(params.startTime).get('month') + 1
			const endmonth = dayjs(params.endTime).get('month') + 1
			const endday = dayjs(params.endTime).get('date')
			const title =
				this.$t('route.settlement') +
				'(' +
				startmonth +
				this.$t('merchant_dailyrateaccount_index_802') +
				startday +
				this.$t('merchant_dailyrateaccount_index_803') +
				'-' +
				endmonth +
				this.$t('merchant_dailyrateaccount_index_802') +
				endday +
				this.$t('merchant_dailyrateaccount_index_803') +
				')'
			localStorage.setItem('title', title)
			this.$api
				.settlementSumExport(params)
				.then((res) => {})
				.catch(() => {
					this.loading = false
				})
		}
	}
}
