import list from '@/mixins/list'
import tier from '@/mixins/tier'
import { mapGetters } from 'vuex'
import dayjs from 'dayjs'
import { routerNames } from '@/utils/consts'
import SelectAgentTree from '@/components/SelectAgentTree'
import router from '@/router'
import TableComp from './components/TableComp'

export default {
	name: routerNames.rateaccount,
	components: { SelectAgentTree, TableComp },
	mixins: [list, tier],
	data() {
		const end = dayjs()
			.endOf('day')
			.subtract(1, 'd')
			.valueOf()
		const start = dayjs()
			.startOf('day')
			.subtract(1, 'd')
			.valueOf()
		return {
			timestamp: Date.now(),
			hadSearch: false,
			loading: false,
			canDownload: true,
			canNoticeTotal: true,
			canNoticeTime: true,
			form: {
				time: [start, end],
				agentId: ''
			},
			tableLists: [{}],
			levels: [],
			currentRow: {},
			currencyList: []
		}
	},
	computed: {
		...mapGetters(['globalDics', 'userInfo']),
		trantype() {
			return this.globalDics.trantype
		}
	},
	methods: {
		loadData() {
			this.loading = true
			this.loadList()
		},
		loadList() {
			const pageData = {
				pageSize: this.pageSize,
				pageIndex: this.pageIndex,
				startTime: this.form.time[0],
				endTime: this.form.time[1],
				type: 1
			}
			const requestData = this.hadSearch
				? {
						searchAgentId: this.form.agentId
				  }
				: {}
			const form = {
				...pageData,
				...requestData
			}
			this.$api
				.pointAccountReport(form)
				.then((_) => {
					this.loading = false
					if (_.code === 200) {
						const { data } = _
						const lists = data.record
						this.total = data.totalRecord
						this.tableLists = [
							{
								opened: false,
								iconLoading: false,
								level: 1,
								data: lists
							}
						]
					}
				})
				.catch(() => {
					this.loading = false
				})
		},
		/**
		 * actType 1为增加 2为修改
		 */
		modifyData(level, params, tableIndex, actType) {
			const extraParams = {
				startTime: this.form.time[0],
				endTime: this.form.time[1],
				searchAgentId: params.agentId,
				order: params.order,
				orderProp: params.orderProp
			}
			this.mixinModifyData(
				level,
				params,
				tableIndex,
				actType,
				'pointAccountReport',
				extraParams
			)
		},
		handleSearch() {
			const extraParams = {
				startTime: this.form.time[0],
				endTime: this.form.time[1]
			}
			this.mixinHandleSearch('pointAccountReport', extraParams)
		},
		handleReset() {
			if (this.$route.query && JSON.stringify(this.$route.query) !== '{}') {
				router.push(this.$route.path, () => {
					this.resetCommon()
				})
			} else {
				this.resetCommon()
			}
		},
		resetCommon() {
			const end = dayjs()
				.endOf('day')
				.subtract(1, 'd')
				.valueOf()
			const start = dayjs()
				.startOf('day')
				.subtract(1, 'd')
				.valueOf()

			this.hadSearch = false

			this.form = {
				time: [start, end],
				agentId: '',
				key: Date.now()
			}
			this.levels = []
			this.timestamp = Date.now()
			this.loadData()
		},
		checkRecord(row) {
			this.$store.dispatch('tagsView/delView', {
				name: routerNames.dotaccount
			})
			this.$nextTick(() => {
				const targetUrl = '/merchant/dotaccount'
				router.push(`${targetUrl}?agentId=${row.agentId}&accountType=3`)
			})
		},
		setCurrentRow(row) {
			const returnQuery = {
				time: [this.form.time[0], this.form.time[1]],
				pageSize: this.pageSize,
				pageIndex: this.pageIndex,
				searchAgentId: this.form.agentId ? this.form.agentId : this.agentId
			}
			// 跳转游戏输赢日结
			this.$store.dispatch('tagsView/delView', {
				name: routerNames.dailyrateaccount
			})
			this.$nextTick(() => {
				const targetUrl = '/merchant/dailyrateaccount'
				this.$router.push(`${targetUrl}`)
				localStorage.setItem('returnQueryAccount', JSON.stringify(returnQuery))
				localStorage.setItem('agentIdRatedeal', row.agentId)
				localStorage.setItem('timeRatedeal', JSON.stringify(this.form.time))
			})
		},
		exportCsv() {
			if (Number(this.total) > 20000) {
				if (this.canNoticeTotal) {
					this.$message({
						message: this.$t('new_2'),
						type: 'error'
					})
					this.canNoticeTotal = false
					setTimeout(() => {
						this.canNoticeTotal = true
					}, 3000)
				}
				return
			}
			if (!this.canDownload) {
				if (this.canNoticeTime) {
					this.$message({
						message: this.$t('new_1'),
						type: 'error'
					})
					this.canNoticeTime = false
					setTimeout(() => {
						this.canNoticeTime = true
					}, 3000)
				}
				return
			}
			this.canDownload = false
			setTimeout(() => {
				this.canDownload = true
			}, 60000)
			const params = {
				startTime: this.form.time ? this.form.time[0] : '',
				endTime: this.form.time ? this.form.time[1] : ''
			}
			const startday = dayjs(params.startTime).get('date')
			const startmonth = dayjs(params.startTime).get('month') + 1
			const endmonth = dayjs(params.endTime).get('month') + 1
			const endday = dayjs(params.endTime).get('date')
			const title =
				this.$t('route.rateaccount') +
				'(' +
				startmonth +
				this.$t('merchant_dailyrateaccount_index_802') +
				startday +
				this.$t('merchant_dailyrateaccount_index_803') +
				'-' +
				endmonth +
				this.$t('merchant_dailyrateaccount_index_802') +
				endday +
				this.$t('merchant_dailyrateaccount_index_803') +
				')'
			localStorage.setItem('title', title)
			this.$api
				.pointAccountSumExport(params)
				.then((res) => {})
				.catch(() => {
					this.loading = false
				})
		}
	}
}
