/* 修复input 背景不协调 和光标变色 */
/* Detail see https://github.com/PanJiaChen/vue-element-admin/pull/927 */

$bg: #283443;
$light_gray: #fff;
$cursor: #fff;
$bgc: #2d3a4b;
$dark_gray: #889aa4;
$light_gray: #eee;

@supports (-webkit-mask: none) and (not (cater-color: $cursor)) {
	.login-container .el-input input {
		color: $cursor;
	}
}

/* reset element-ui css */
.login-container {
	min-height: 100%;
	width: 100%;
	background-color: $bgc;
	overflow: hidden;
	background: url('../../assets/login_imgs/login_bg.jpg') no-repeat center center;
	background-size: cover;
	.login-logo {
		width: 32px !important;
		height: 32px !important;
		vertical-align: -6px !important;
	}
	.login-form {
		position: relative;
		width: 600px;
		max-width: 100%;
		padding: 160px 35px 0;
		margin: 0 auto;
		overflow: hidden;
	}
	.logo {
		text-align: center;
		margin-bottom: 19px;
	}
	.login-content {
		padding: 8% 12%;
		min-height: 560px;
		background: url('../../assets/login_imgs/form_bg.png') no-repeat;
		background-size: cover;

		.svg-icon {
			vertical-align: -0.15em;
		}
	}
	h3 {
		margin: 0;
		margin-bottom: 16px;
		text-align: center;
		font-family: MicrosoftYaHei;
		font-size: 30px;
		font-weight: normal;
		font-stretch: normal;
		letter-spacing: 0px;
		color: #ffffff;
		line-height: 50px;

		img {
			float: left;
			position: relative;
			left: -10px;
		}
		span {
			display: inline-block;
		}

		.logo-text {
			padding: 0 2px;
		}
	}
	.el-input {
		display: inline-block;
		height: 47px;
		width: 75%;
		input {
			background: transparent;
			border: 0px;
			-webkit-appearance: none;
			border-radius: 0px;
			padding: 12px 5px 12px 15px;
			color: $light_gray;
			height: 47px;
			line-height: 1.5;
			caret-color: $cursor;

			&:-webkit-autofill {
				box-shadow: 0 0 0px 1000px $bg inset !important;
				-webkit-text-fill-color: $cursor !important;
			}
		}
	}
	.el-form-item {
		box-sizing: border-box;
		width: auto;
		height: 52px;
		margin: 0 auto;
		margin-bottom: 25px;
		background-color: #2e3949;
		color: #454545;
		border-radius: 10px;
		border: 2px solid transparent;
	}
	.el-form-item:focus-within .svg-active {
		color: #0b7dfa;
	}
	.el-form-item:focus-within .el-form-item__error {
		display: none;
	}
	.el-form-item:focus-within {
		background-color: #101221;
		border: 2px solid #0b7dfa;
		border-radius: 10px;
	}
	.login-btn {
		background: url('../../assets/login_imgs/submit_bg.png') no-repeat;
		background-size: cover;
		height: 51px;
		cursor: pointer;
		border-radius: 10px;
		text-align: center;
		font-family: MicrosoftYaHei;
		font-size: 20px;
		color: #ffffff;
		line-height: 51px;
	}
	.aActive {
		color: #fff !important;
	}
	.switch-language a {
		margin-top: 26px;
		font-family: MicrosoftYaHei;
		text-decoration: underline;
		font-size: 16px;
		font-weight: normal;
		font-stretch: normal;
		letter-spacing: 0px;
		color: #8a90a5;
	}
	.svg-icon {
		width: 18px;
	}
	.svg-container {
		padding: 1px 5px 6px 15px;
		color: $dark_gray;
		vertical-align: middle;
		width: 30px;
		display: inline-block;
	}

	.show-pwd {
		position: absolute;
		right: 10px;
		top: 7px;
		font-size: 16px;
		color: $dark_gray;
		cursor: pointer;
		user-select: none;
	}
	.user-agreement {
		color: #fff;
		text-decoration: underline;
	}
	.version {
		position: fixed;
		right: 10px;
		bottom: 10px;
		color: #fff;
	}

	.kaptcha {
		transform: translateY(3px);
	}
}
