import list from '@/mixins/list'
import { routerNames } from '@/utils/consts'
import router from '@/router'

export default {
	name: routerNames.activityDetail,
	components: { },
	mixins: [list],
	data() {
		return {
			hadSearch: false,
			canNoticeTotal: true,
			canNoticeTime: true,
			loading: false,
			canDownload: true,
			amountData: {},
			form: {
				playerId: '',
				bizId: ''
			},
			first: true,
			summary: {}
		}
	},
	mounted() {},
	destroyed() {},
	methods: {
		loadData() {
			this.loading = true
				this.form.playerId = localStorage.getItem('playerId')
				this.form.bizId = localStorage.getItem('bizId')
			this.$api
				.activeTaskDetailList(this.form)
				.then((_) => {
					this.loading = false
					if (_.code === 200) {
						const data = _.data
						this.list = data.list || []
						this.total = data.totalRecord
						this.summary = data.summaryDto
					}
				})
				.catch(() => {
					this.loading = false
				})
		},
		getSummaries(param) {
			const { columns } = param
			const sums = []
			columns.forEach((column, index) => {
				if (index === 0) {
					sums[index] = this.$t('members_report_index_723')
					return
				}
				if (index === 1) {
					sums[index] = this.$t('a_47') + ':' + this.summary['activityVaildAmount']
					return
				}
				if (index === 2) {
					sums[index] = this.$t('a_55') + '' + this.summary['completeVaildAmount']
					return
				}
			})
			return sums
		},

		handleCurrentChange(val) {
			this.loadData()
		},
		goPrev() {
			router.push(`/operationManage/activity`)
		}
	}
}
